"""SQLite 存储层，负责管理图像/视频生成记录."""

from __future__ import annotations

import json
import sqlite3
import threading
from concurrent.futures import Future, TimeoutError
from queue import Queue
from datetime import datetime
from pathlib import Path
from typing import Any, Callable, Dict, List, Optional

from loguru import logger


class SQLiteHandler:
    """封装 SQLite 持久化逻辑."""

    _DEFAULT_TIMEOUT_SECONDS = 30.0

    def __init__(self, db_path: Path, storage_root: Optional[Path] = None):
        """初始化数据库连接并创建所需表结构.

        Args:
            db_path: 数据库文件路径
            storage_root: 存储根目录，用于将相对路径转换为绝对路径
        """

        self.db_path = Path(db_path)
        self.db_path.parent.mkdir(parents=True, exist_ok=True)
        self.storage_root = storage_root

        # 并发策略（Q002-方案B）：单线程 DB Worker + 队列串行执行
        self._closed = False
        self._queue: Queue[Optional[tuple[Future, Callable[[sqlite3.Connection], Any]]]] = Queue()
        self._init_future: Future[bool] = Future()

        self._worker_thread = threading.Thread(
            target=self._run_worker,
            name=f"SQLiteHandler[{self.db_path.name}]",
            daemon=True,
        )
        self._worker_thread.start()

        try:
            self._init_future.result(timeout=self._DEFAULT_TIMEOUT_SECONDS)
        except Exception as exc:
            logger.error("Failed to initialize SQLite database {}: {}", self.db_path, exc)
            raise RuntimeError("Unable to initialize SQLite database") from exc

    def _run_worker(self) -> None:
        connection: Optional[sqlite3.Connection] = None
        try:
            connection = sqlite3.connect(self.db_path)
            connection.row_factory = sqlite3.Row
            connection.execute("PRAGMA foreign_keys = ON;")
            self._create_tables(connection)
        except Exception as exc:
            self._init_future.set_exception(exc)
            if connection:
                try:
                    connection.close()
                except Exception:
                    pass
            return

        self._init_future.set_result(True)

        while True:
            item = self._queue.get()
            try:
                if item is None:
                    break
                future, fn = item
                if not future.set_running_or_notify_cancel():
                    continue
                try:
                    future.set_result(fn(connection))
                except Exception as exc:
                    future.set_exception(exc)
            finally:
                self._queue.task_done()

        if connection:
            try:
                connection.close()
            except sqlite3.Error as exc:
                logger.error("Failed to close SQLite connection: {}", exc)

    def _call(self, operation: str, fn: Callable[[sqlite3.Connection], Any], default: Any):
        if self._closed:
            return default
        future: Future = Future()
        self._queue.put((future, fn))
        try:
            return future.result(timeout=self._DEFAULT_TIMEOUT_SECONDS)
        except TimeoutError:
            logger.error("SQLite operation timed out: {}", operation)
            return default
        except Exception as exc:
            logger.error("SQLite operation failed {}: {}", operation, exc)
            return default

    def _create_tables(self, connection: sqlite3.Connection) -> None:
        """初始化数据库表结构."""

        schema = """
        CREATE TABLE IF NOT EXISTS generations (
            id TEXT PRIMARY KEY,
            content_type TEXT NOT NULL,
            prompt TEXT,
            model TEXT,
            status TEXT DEFAULT 'completed',
            file_path TEXT,
            relative_path TEXT,
            thumbnail_path TEXT,
            parameters TEXT,
            metadata TEXT,
            created_at TEXT,
            updated_at TEXT
        );

        CREATE TABLE IF NOT EXISTS reference_assets (
            id TEXT PRIMARY KEY,
            generation_id TEXT NOT NULL,
            asset_path TEXT,
            relative_path TEXT,
            asset_type TEXT,
            order_index INTEGER DEFAULT 0,
            metadata TEXT,
            FOREIGN KEY (generation_id) REFERENCES generations(id) ON DELETE CASCADE
        );

        CREATE INDEX IF NOT EXISTS idx_generations_type ON generations(content_type);
        CREATE INDEX IF NOT EXISTS idx_generations_created ON generations(created_at DESC);
        CREATE INDEX IF NOT EXISTS idx_refs_generation ON reference_assets(generation_id);
        """

        try:
            with connection:
                connection.executescript(schema)
        except sqlite3.Error as exc:
            logger.error("Failed to create initial tables: {}", exc)
            raise RuntimeError("Unable to create SQLite tables") from exc

    @staticmethod
    def _now() -> str:
        return datetime.utcnow().isoformat()

    @staticmethod
    def _dump_json(data: Any) -> Optional[str]:
        if data is None:
            return None

        try:
            return json.dumps(data)
        except (TypeError, ValueError) as exc:
            logger.error("Failed to serialize JSON field: {}", exc)
            return None

    @staticmethod
    def _load_json(payload: Optional[str]) -> Optional[Any]:
        if not payload:
            return None

        try:
            return json.loads(payload)
        except json.JSONDecodeError as exc:
            logger.error("Failed to decode JSON payload: {}", exc)
            return None

    def _row_to_dict(self, row: sqlite3.Row) -> Dict[str, Any]:
        result = dict(row)
        result["parameters"] = self._load_json(result.get("parameters"))
        result["metadata"] = self._load_json(result.get("metadata"))

        # 将相对路径转换为绝对路径
