def bind_repository(self, repo_manager):
    self._repo = repo_manager

def clear(self):
    self._next_cursor = None
    self._loading = False  # 重置加载状态，防止后续加载被阻塞
    self.model.clear()
    self._placeholder_items.clear()

def load_initial(self):
    if self._loading or not self._repo:
        return
    self._loading = True

    def _task():
        return self._repo.get_records(page_size=self._page_size, cursor=None, record_type='video')

    def _on_done(result):
        try:
            items = result.get('items', []) if isinstance(result, dict) else (result or [])
            self._next_cursor = result.get('next_cursor') if isinstance(result, dict) else None
            if items:
                self.items_appended.emit(items)
            if self._repo:
                def _count():
                    return self._repo.get_total_count('video')
                def _on_total(total):
                    self._total_count = total
                    self._schedule_status_update()
                task_runner.submit(_count, key="video-history-view:total", on_done=_on_total)
        finally:
            self._loading = False

    task_runner.submit(_task, key="video-history-view:init", on_done=_on_done)

def load_next(self):
    if self._loading or not self._repo or not self._next_cursor:
        return
    self._loading = True

    def _task():
        return self._repo.get_records(page_size=self._page_size, cursor=self._next_cursor, record_type='video')

    def _on_done(result):
        try:
            items = result.get('items', []) if isinstance(result, dict) else (result or [])
            self._next_cursor = result.get('next_cursor') if isinstance(result, dict) else None
            if items:
                self.items_appended.emit(items)
                self._schedule_status_update()
        finally:
            self._loading = False

    task_runner.submit(_task, key=f"video-history-view:next:{self._next_cursor}", on_done=_on_done)

def _on_scroll(self, value: int):
    sb = self.list.verticalScrollBar()
    if sb.maximum() <= 0:
        return
    if value >= int(sb.maximum() * self._prefetch_threshold):
        self.load_next()

def _on_clicked(self, index):
    rec = self.model.get_record(index.row())
    # 触发缩略图重试
    self.model.retry_thumbnail(index.row())
    if self._click_cb and rec:
        # 在 rec 中附加视图索引，用于详情页导航
        rec_with_index = dict(rec) if isinstance(rec, dict) else rec
        rec_with_index['_view_index'] = index.row()
        self._click_cb(rec_with_index)

def add_placeholder_item(self, task_id: str, prompt: str = "") -> int:
    """在列表顶部插入视频占位项。"""
    if not task_id:
        return -1
    placeholder_data = {
        'file_id': task_id,
        'id': task_id,
        'task_id': task_id,

def _on_pending_recovery_failed(self, message: str) -> None:
    self._pending_recovery_inflight = False
    logger.debug(f"pending recovery failed: {message}")

def _init_history_incremental_loading(self):
    """初始化瀑布流增量加载功能"""
    try:
        if hasattr(self, 'video_history_view') and self._repository_manager:
            # 绑定Repository管理器到瀑布流组件
            self.video_history_view.set_repository_manager(self._repository_manager)
            logger.info("VideoModeWidget: 瀑布流增量加载功能初始化完成")
        else:
            logger.warning("VideoModeWidget: 瀑布流组件或Repository管理器未就绪，跳过增量加载初始化")
    except Exception as e:
        logger.error(f"VideoModeWidget: 初始化瀑布流增量加载功能失败: {e}")

def fix_file_path(self, file_path: str) -> str:
    """
    新路径绝对主义的文件路径处理
    只处理当前存储路径下的文件，忽略其他路径的文件

    Args:
        file_path: 原始文件路径

    Returns:
        有效的文件路径，如果文件不在当前路径或不存在则返回None
    """
    if not file_path:
        return None

    try:
        from config_api import get_storage_dir
        current_storage = get_storage_dir()

        # 只处理当前存储路径下的文件
        if not file_path.startswith(current_storage):
            logger.debug(f"忽略非当前路径文件: {file_path}")
            return None

        # 验证文件是否存在
        if not os.path.exists(file_path):
            logger.debug(f"文件不存在，忽略: {file_path}")
            return None

        return file_path

    except Exception as e:
        logger.error(f"文件路径处理失败: {e}")
        return None

def showEvent(self, event):
    """页面显示事件 - 自动加载历史记录"""
    super().showEvent(event)
    # 延迟加载历史记录，确保页面完全初始化
    QTimer.singleShot(100, self._auto_load_history)

def _auto_load_history(self):
    """自动加载历史记录"""
    try:
        # 检查是否已经有数据，避免重复加载
        if self.use_virtual_video_view:
            if hasattr(self, 'video_history_view') and getattr(self.video_history_view, 'model', None):
                # 只有视图完全为空时才加载
                if self.video_history_view.model.rowCount() == 0:
                    logger.debug("视频页面显示时自动加载历史记录（虚拟化视图）")
                    self.video_history_view.bind_repository(self.get_repository_manager())
                    self.video_history_view.load_initial()
                else:
                    count = self.video_history_view.model.rowCount()
                    # 刷新最新记录（包括pending）确保数据同步
                    try:
                        self.video_history_view.prepend_latest(page_size=20)
                        logger.debug(f"[Pending追踪] 视频页面已有{count}条数据，已刷新最新记录")
                    except Exception as refresh_err:
                        logger.debug(f"刷新最新记录失败: {refresh_err}")
        else:
            if len(self.video_history_view.image_list) == 0:
                logger.debug("视频页面显示时自动加载历史记录（瀑布流）")
                self.load_history_videos()

    self.setWrapping(False) # <--- Critical for horizontal scroll
    self.setResizeMode(QListWidget.Adjust)
    self.setSpacing(6)

    # Scrllbars
    self.setVerticalScrollBarPolicy(Qt.ScrollBarAlwaysOff)
    self.setHorizontalScrollBarPolicy(Qt.ScrollBarAsNeeded)

    # Delegate
    self.setItemDelegate(IntegratedReferenceDelegate(self))

    # Style
    self.setStyleSheet("""
        QListWidget {
            background: transparent;
            outline: none;
        }
        QListWidget::item {
            border-radius: 6px;
        }
        QListWidget::item:selected {
            background: transparent; /* Delegate handles it */
        }
    """)

    # Enable Mouse Tracking for Hovers
    self.setMouseTracking(True)

def mousePressEvent(self, event):
    # Handle Delete Click
    index = self.indexAt(event.pos())
    if index.isValid():
        rect = self.visualRect(index)
        # Check Top-Right Delete Zone
        # Logic matches Delegate's drawing
        badge_margin = 4
        del_size = 18
        # Calculate Delete Rect relative to Item Rect
        # In Delegate: rect.right() - margin - size
        del_rect = QRect(
            rect.right() - badge_margin - del_size,
            rect.y() + badge_margin,
            del_size,
            del_size
        )

        if del_rect.contains(event.pos()):
            # Clicked Delete
            self.itemDeleted.emit(index.row())
            return

        # Normal selection
        super().mousePressEvent(event)
    else:
        # Clicked Empty Space -> Trigger Upload
        self.uploadTriggered.emit()

def dragEnterEvent(self, event: QDragEnterEvent):
    if event.mimeData().hasUrls():
        event.accept()
    else:
        super().dragEnterEvent(event)

def dragMoveEvent(self, event: QDragEnterEvent):
    if event.mimeData().hasUrls():
        event.accept()
    else:
        super().dragMoveEvent(event)

def dropEvent(self, event: QDropEvent):
    if event.mimeData().hasUrls():
        event.accept()
        paths = []
        for url in event.mimeData().urls():
            path = url.toLocalFile()
            if path:
                paths.append(path)
        if paths:
            self.filesDropped.emit(paths)
    else:
        super().dropEvent(event)

def paintEvent(self, event):
    super().paintEvent(event)
    if self.count() == 0:
        painter = QPainter(self.viewport())
        painter.setRenderHint(QPainter.Antialiasing)

        rect = self.viewport().rect()

        # Colors - 动态获取主题颜色
        colors = unified_style_system.get_colors()
        text_color = QColor(
            colors.get('palette.text.disabled')
            or colors.get('palette.text.tertiary')
            or colors.get('palette.text.secondary')
            or colors.get('text_secondary')
            or colors.get('palette.text.primary')
            or colors.get('text')
        )

