def format_time(self, time_str: str) -> str:
    """格式化时间显示"""
    if not time_str:
        return '未知时间'
    try:
        dt = datetime.fromisoformat(time_str.replace('Z', '+00:00'))
        return dt.strftime('%Y-%m-%d %H:%M:%S')
    except:
        return time_str[:19] if len(time_str) > 19 else time_str

def format_file_size(self, size) -> str:
    """格式化文件大小显示"""
    # 处理字符串类型的size
    try:
        size = float(size) if size else 0
    except (ValueError, TypeError):
        return '未知大小'

    if size == 0:
        return '未知大小'

    for unit in ['B', 'KB', 'MB', 'GB']:
        if size < 1024.0:
            return f"{size:.1f} {unit}"
        size /= 1024.0
    return f"{size:.1f} TB"

def setup_shortcuts(self):
    """设置键盘快捷键"""
    # 移除所有QShortcut，统一使用keyPressEvent处理
    # 避免快捷键冲突问题
    pass

def _ensure_focus(self):
    """确保窗口获得焦点"""
    self.activateWindow()
    self.raise_()
    self.setFocus()
    logger.debug(f"[FOCUS] ImageDialog窗口焦点设置完成，hasFocus: {self.hasFocus()}")

def toggle_fullscreen(self):
    """切换全屏模式"""
    if self.is_fullscreen:
        self.exit_fullscreen()
    else:
        self.enter_fullscreen()

def enter_fullscreen(self):
    """进入全屏模式"""

colors = unified_style_system.get_colors()
bg_tag = colors.get('palette.neutral.bg_surface') or colors.get('palette.neutral.bg_card') or colors.get('surface')
border_tag = colors.get('palette.neutral.border_subtle') or colors.get('border')
text_tag = colors.get('text') or colors.get('palette.text.primary')
hover_bg = colors.get('palette.neutral.bg_hover') or bg_tag
hover_border = colors.get('palette.brand.main') or colors.get('primary') or border_tag

for label, value in info_items:
    label_widget = QLabel(f"<b>{label}:</b>")
    label_widget.setStyleSheet("margin: 0px; padding: 2px 0px;")
    unified_style_system.apply_to_widget(label_widget)
    self.params_layout.addWidget(label_widget)

    # 使用可点击的标签
    value_clickable = ClickableLabel(str(value))
    value_clickable.set_text_with_ellipsis(str(value), int(self.width() * 0.25))
    # File path is long and can otherwise expand to fill the panel, overlapping the prompt area.
    # Keep it single-line with ellipsis and fixed height.
    if label == "文件路径":
        value_clickable.setWordWrap(False)
        value_clickable.setSizePolicy(QSizePolicy.Expanding, QSizePolicy.Fixed)
        value_clickable.setFixedHeight(32)
        value_clickable.setToolTip(str(value))
    else:
        value_clickable.setWordWrap(True)
    # 使用动态样式
    value_clickable.setStyleSheet(f"""
        QLabel {{
            margin: 0px; 
            padding: 6px; 
            background-color: {bg_tag}; 
            color: {text_tag};
            border: 1px solid {border_tag};
            border-radius: 4px;
        }}
        QLabel:hover {{
            background-color: {hover_bg};
            border-color: {hover_border};
        }}
    """)
    unified_style_system.apply_to_widget(value_clickable)
    value_clickable.clicked.connect(self.copy_text_to_clipboard)
    self.params_layout.addWidget(value_clickable)

    # 保存标签引用
    self.param_labels.append((label_widget, value_clickable))

# Ensure remaining vertical space doesn't get assigned to the last label widget.
self.params_layout.addStretch(1)

