    self._refresh_reference_gallery()

def _update_theme(self):
    """响应主题变化"""
    colors = unified_style_system.get_colors()

    # Apply theme to self (Dialog)
    # Note: In some OS/Qt versions, QDialog styling needs explicit background
    bg_color = colors.get('palette.neutral.bg_surface') or colors.get('palette.neutral.bg_app') or colors.get('background')
    text_color = colors.get('text') or colors.get('palette.text.primary')
    bg_card = colors.get('palette.neutral.bg_card') or colors.get('surface') or bg_color
    bg_input = colors.get('palette.neutral.bg_input') or colors.get('palette.neutral.bg_card') or bg_card
    border_subtle = colors.get('palette.neutral.border_subtle') or colors.get('border')
    brand = colors.get('palette.brand.main') or colors.get('primary') or text_color

    self.setStyleSheet(f"""
        QDialog {{
            background-color: {bg_color};
            color: {text_color};
        }}
        QLabel {{
            color: {text_color};
        }}
        QWidget#ParamsWidget {{
             background-color: {bg_card};
             border-left: 1px solid {border_subtle};
        }}
    """)

    # Update specific controls directly
    if hasattr(self, 'prompt_text_edit'):
         self.prompt_text_edit.setStyleSheet(f"""
            QTextEdit {{
                background-color: {bg_input};
                color: {text_color};
                border: 1px solid {border_subtle};
                border-radius: 8px;
                padding: 8px;
            }}
            QTextEdit:focus {{
                border-color: {brand};
            }}
         """)

    # Manually update param labels that were stored
    if hasattr(self, 'param_value_labels'):
        for label, value_label in self.param_value_labels.items():
            value_label.setStyleSheet(f"""
                QLineEdit {{
                    background-color: {bg_input};
                    color: {text_color};
                    border: 1px solid {border_subtle};
                    padding: 4px;
                    border-radius: 8px;
                }}
                QLineEdit:focus {{
                    border-color: {brand};
                }}
            """)

    # Update thumbnail background if visible
    if hasattr(self, 'thumbnail_label'):
         player_bg = bg_color
         self.thumbnail_label.setStyleSheet(f"""
            QLabel {{
                border: none;
                background-color: {player_bg};
                color: {text_color};
            }}
         """)

    # Reapply button styles (including fixed components)
    if hasattr(self, 'copy_prompt_btn'):
        unified_style_system.apply_to_widget(self.copy_prompt_btn)
    if hasattr(self, 'fullscreen_btn'):
        unified_style_system.apply_to_widget(self.fullscreen_btn)
    if hasattr(self, 'download_btn'):
        unified_style_system.apply_to_widget(self.download_btn)
    if hasattr(self, 'delete_btn'):
        unified_style_system.apply_to_widget(self.delete_btn, extra={'variant': 'danger'})

