def update_video_params(self):
    """更新视频参数显示（切换视频时调用）"""
    try:
        # 解析生成参数
        params = self.video_data.get('parameters', '{}')
        if isinstance(params, str):
            try:
                params = json.loads(params)
            except:
                params = {}

        # 从 parameters 中获取参数（兼容顶级字段）
        duration = params.get('duration') or self.video_data.get('duration', '未知')
        file_size = params.get('file_size') or self.video_data.get('file_size', 0)

        # 重新构建参数信息
        info_items = [
            ("分辨率", build_resolution_text(self.video_data)),
            ("时长", f"{duration}秒"),
            ("模型", get_model_display_name(pick_model_code(self.video_data))),
            ("生成时间", self.format_time(self.video_data.get('created_at', ''))),
            ("文件大小", self.format_file_size(file_size)),
            ("文件路径", self.video_data.get('absolute_path', '') or self.video_data.get('file_path', '')),
        ]

        # 更新已保存的标签
        for label, new_value in info_items:
            if label in self.param_value_labels:
                w = self.param_value_labels[label]
                text = str(new_value)
                try:
                    if hasattr(w, "set_text_with_ellipsis"):
                        w.set_text_with_ellipsis(text, int(self.width() * 0.25))
                    else:
                        w.setText(text)
                    if label == "文件路径":
                        w.setToolTip(text)
                except Exception:
                    pass


def _setup_config_monitoring(self):
    """设置配置监听定时器"""
    from PySide6.QtCore import QTimer
    self._config_check_timer = QTimer()
    self._config_check_timer.timeout.connect(self._check_config_changes)
    self._config_check_timer.start(2000)  # 每2秒检查一次配置变化

def _check_config_changes(self):
    """检查配置变化（兜底机制，主渠道是 SettingsPage.settings_applied -> MainWindow.on_storage_path_changed）。"""
    try:
        from config_api import get_storage_dir
        current_path = get_storage_dir()

        if self._current_storage_path != current_path:
            logger.info(f"[定时器检测] 视频存储路径变更: {self._current_storage_path} -> {current_path}")
            # 先更新本地记录，避免每2秒重复触发
            self._current_storage_path = current_path

            # 如果主信号已经完成了重绑/加载，这里不再做任何同步重初始化
            try:
                view = getattr(self, 'video_history_view', None)
                model = getattr(view, 'model', None) if view else None
                if model and model.rowCount() > 0:
                    return
            except Exception:
                pass

            # 兜底：仅在页面无数据时执行重绑 + 初次加载
            try:
                self._update_storage_manager(current_path)
            except Exception:
                pass
            try:
                self._update_repository_manager(current_path)
            except Exception:
                pass
            try:
                view = getattr(self, 'video_history_view', None)
                if view and hasattr(view, 'clear') and hasattr(view, 'bind_repository') and hasattr(view, 'load_initial'):
                    view.clear()
                    view.bind_repository(self.get_repository_manager())
                    view.load_initial()
                    return
            except Exception:
                pass

            # 最后兜底：走旧逻辑（内部会选择虚拟化/传统加载分支）
            self.load_history_videos()
    except Exception as e:

