@Slot(list)
def _apply_prepend_items(self, items: list):
    try:
        if items:
            self.model.prepend_items(items)
    except Exception:
        pass

@Slot(str)
def _apply_update_status(self, text: str):
    try:
        if self._status_cb and isinstance(text, str):
            self._status_cb(text)
    except Exception:
        pass

def _existing_ids(self) -> set:
    ids = set()
    try:
        for i in range(self.model.rowCount()):
            rec = self.model.get_record(i) or {}
            fid = rec.get('file_id') or rec.get('id') or rec.get('file_path')
            if fid:
                ids.add(str(fid))
    except Exception:
        pass
    return ids

def _normalize_pending_records(self, records: List[Dict[str, Any]]) -> List[Dict[str, Any]]:
    """将数据库中的pending记录转换为占位样式。"""
    normalized: List[Dict[str, Any]] = []
    for item in records or []:
        if not isinstance(item, dict):
            continue
        record = dict(item)
        status_text = str(record.get('status') or '').lower()
        if status_text == 'pending':
            record['is_placeholder'] = True
            task_id = record.get('task_id') or record.get('file_id') or record.get('id')
            if task_id:
                record['task_id'] = str(task_id)
            record.setdefault('progress', 0)
        normalized.append(record)
    return normalized

def prepend_latest(self, page_size: int = 40):
    if self._loading or not self._repo:
        return
    self._loading = True

    # 获取当前最新记录的时间戳，用于增量检查
    latest_timestamp = None
    if self.model.rowCount() > 0:
        first_record = self.model.get_record(0)
        if first_record:
            latest_timestamp = first_record.get('created_at')

    def _task():
        # 获取更大范围的最新记录，确保不遗漏
        extended_page_size = max(page_size, 60)  # 至少获取60条记录
        return self._repo.get_records_batch(0, extended_page_size, 'image')

    def _on_done(items: List[Dict[str, Any]]):
        try:
            normalized_items = self._normalize_pending_records(items or [])
            new_items = []
            exist_ids = self._existing_ids()

            for r in normalized_items:
                fid = r.get('file_id') or r.get('id') or r.get('file_path')
                if not fid:
                    continue

                # 如果有时间戳，优先使用时间戳判断是否为新记录
                if latest_timestamp:
                    record_time = r.get('created_at')
                    if record_time and record_time > latest_timestamp:
                        new_items.append(r)
                        continue

                # 回退到ID检查（但更宽松）
                if str(fid) not in exist_ids:
                    new_items.append(r)

                            # 调试日志
            logger.debug(f"[虚拟化视图] prepend_latest: 获取到 {len(items or [])} 条记录")
            logger.debug(f"[虚拟化视图] 当前已有 {len(exist_ids)} 个ID")
            logger.debug(f"[虚拟化视图] 筛选出 {len(new_items)} 条新记录")
            if new_items:
                # 按时间排序，确保最新的在前面
                new_items.sort(key=lambda x: x.get('created_at', ''), reverse=True)
                # 在主线程执行模型更新与状态变更
                self.items_prepended.emit(new_items)
                self.update_status.emit(f"新增 {len(new_items)} 张，累计 {self.model.rowCount() + len(new_items)} 张图片")
            else:
                # 即使没有新项目，也更新状态显示
                self.update_status.emit(f"已是最新，累计 {self.model.rowCount()} 张图片")
        finally:

            self._loading = False

    task_runner.submit(_task, key="image-history-view:prepend", on_done=_on_done)

def _apply_column_width(self, initial: bool = False):
    try:
        vw = self.list.viewport().width()
        if vw <= 0 and initial:
            # 初次可能未布局完成，使用一个保底宽度
            vw = DpiScaler.px(1200)
        layout = compute_card_layout(vw)
        col_w = layout['column_width']

        # 优化：只在列宽真正改变时才重绘
        if self._last_column_width == col_w and not initial:
            return

        self._last_column_width = col_w
        self.model.set_column_width(col_w)
        self._delegate.set_column_width(col_w)
        # 强制几何更新
        self.list.updateGeometries()
        if self.model.rowCount() > 0:
            self.model.layoutAboutToBeChanged.emit()
            self.model.layoutChanged.emit()
    except Exception:
        pass

def resizeEvent(self, event):
    super().resizeEvent(event)
    # 移除激进的实时重算，使用防抖
    self._resize_timer.start()

def set_status_callback(self, cb: Callable[[str], None]):
    self._status_cb = cb

def set_item_click_callback(self, cb: Callable[[dict], None]):
    self._click_cb = cb

def bind_repository(self, repo_manager):
    self._repo = repo_manager

def clear(self):
    self._next_cursor = None
    self._loading = False  # 重置加载状态，防止后续加载被阻塞
    self._load_token += 1  # 使所有在途回调失效
    self.model.clear()
    self._placeholder_items.clear()

def load_initial(self, page_size: int = 40):
    if self._loading or not self._repo:
        return
    self._page_size = page_size
    self._loading = True
    self._load_token += 1
    token = self._load_token

    def _task():
        return self._repo.get_records(page_size=self._page_size, cursor=None, record_type='image')

    def _on_done(result):
        try:
            if token != self._load_token:
                return
            items = result.get('items', []) if isinstance(result, dict) else (result or [])
            items = self._normalize_pending_records(items)
            self._next_cursor = result.get('next_cursor') if isinstance(result, dict) else None
            # 主线程追加
            if items:
                self.items_appended.emit(items)
            # 统计总数并在主线程更新状态
            if self._repo:
                def _count():
                    return self._repo.get_total_count('image')
                def _on_total(total):
                    if token != self._load_token:
                        return
                    self.update_status.emit(f"已加载 {self.model.rowCount()} 张图片（共 {total} 张）")
                task_runner.submit(_count, key=f"image-history-view:total:{token}", on_done=_on_total)
        finally:
            if token == self._load_token:
                self._loading = False

    def _on_error(exc: BaseException):
        try:
            if token != self._load_token:
                return
            self.update_status.emit(f"加载图片历史失败: {exc}")
        finally:
            if token == self._load_token:
                self._loading = False

    task_runner.submit(_task, key=f"image-history-view:init:{token}", on_done=_on_done, on_error=_on_error)

def load_next(self):
    if self._loading or not self._repo or not self._next_cursor:
        return
    self._loading = True
    self._load_token += 1
    token = self._load_token

