    def validate_image(self, file_path: str) -> bool:
        """验证图片文件"""
        try:
            # 使用新的图片格式验证器
            from utils.image_format_validator import image_validator

            validation_result = image_validator.validate_image(file_path)

            if not validation_result['valid']:
                # 显示详细错误信息
                self.show_styled_message("图片验证失败", validation_result['error'], "warning")
                return False

            if validation_result['needs_conversion']:
                # 自动转换，不再询问用户
                self.status_label.setText("检测到不支持的图片格式，正在自动转换为JPEG格式...")
                # 这里验证器会在后续的prepare_image_for_api调用中自动处理转换

            return True

        except Exception as e:
            self.show_styled_message("验证错误", f"图片验证过程中发生错误：\n{str(e)}", "critical")
            return False

