    def update_button_rotation(self):
        """更新按钮旋转角度"""
        self.rotation_angle = (self.rotation_angle + 10) % 360
        # 更新按钮文本，使用不同的旋转字符来模拟旋转效果
        rotation_chars = ["⟳", "⟲", "⟳", "⟲"]
        char_index = (self.rotation_angle // 90) % len(rotation_chars)

        # 获取当前状态文本，如果没有设置则使用默认值
        current_status = getattr(self, 'current_generation_status', '生成中...')
        self.generate_button.setText(f"{rotation_chars[char_index]} {current_status}")

    def update_generation_status(self, status_text: str):
        """更新生成状态文本"""
        self.current_generation_status = status_text
        # 如果正在旋转动画中，状态会在下次旋转更新时显示
        # 如果没有动画，直接更新按钮文本
        if not hasattr(self, 'rotation_timer') or not self.rotation_timer.isActive():
            self.generate_button.setText(status_text)

    def stop_button_loading_animation(self):
        """停止按钮加载动画"""
        if hasattr(self, 'rotation_timer'):
            self.rotation_timer.stop()
            self.rotation_timer.deleteLater()
            delattr(self, 'rotation_timer')

        # 清除状态文本
        if hasattr(self, 'current_generation_status'):
            delattr(self, 'current_generation_status')

