            self.set_buttons_enabled(True)

            if result.get('success', False):
                # 保存配置
                success = app_config.set_custom_storage_path(self.current_path, True)

                if success:
                    self.show_success("设置应用成功！")

                    # 更新状态显示
                    self.is_custom_enabled = True
                    self.update_status_display()

                    # 发送信号
                    self.settings_applied.emit(self.current_path)
                else:
                    self.show_error("保存配置失败")
            else:
                error_msg = result.get('error', '未知错误')
                self.show_error(f"初始化失败: {error_msg}")

        except Exception as e:
            self.hide_progress()
            self.set_buttons_enabled(True)
            self.show_error(f"应用设置时出错: {e}")

    def on_init_error(self, error_msg: str):
        """初始化错误回调"""
        self.hide_progress()
        self.set_buttons_enabled(True)
        self.show_error(f"初始化失败: {error_msg}")

    def reset_settings(self):
        """重置设置"""
        try:
            reply = QMessageBox.question(
                self,
                "确认重置",
                "确定要重置为默认存储位置吗？\n\n现有文件不会被删除，但新文件将保存到默认位置。",
                QMessageBox.Yes | QMessageBox.No,
                QMessageBox.No
            )

            if reply == QMessageBox.Yes:
                # 禁用自定义存储
                app_config.disable_custom_storage()

                # 重置UI状态
                self.current_path = ""
                self.is_custom_enabled = False
                self.path_display.setText("")
                self.path_display.setPlaceholderText("当前使用默认存储路径")

                # 更新状态显示
                self.update_status_display()

                # 发送信号
                self.settings_reset.emit()

                self.show_success("已重置为默认存储位置")

        except Exception as e:
            self.show_error(f"重置设置失败: {e}")

    def save_api_keys(self):
        """保存 API 密钥到用户凭据并刷新进程变量"""
        try:
            volc_val = (self.volc_key_input.text() or "").strip()
            zhipu_val = (self.zhipu_key_input.text() or "").strip()

            if volc_val:
                config_manager.update_api_key("volcengine", volc_val)
            if zhipu_val:
                config_manager.update_api_key("zhipu", zhipu_val)

            # 清空输入框，但更新状态
            self.volc_key_input.clear()
            self.zhipu_key_input.clear()
            self.update_status_display()

            self.show_success("密钥已保存（已写入本机凭据文件并生效）")
        except Exception as e:
            self.show_error(f"保存密钥失败: {e}")

    def test_api_keys(self):
        """测试 API 密钥有效性（仅检查是否配置）"""
        try:
            ok, missing, message = validate_api_keys()
            if ok:
                self.show_success("API 密钥检查通过")
            else:
                QMessageBox.warning(self, "API 密钥未配置", message)
        except Exception as e:
            self.show_error(f"测试失败: {e}")

