from enum import Enum
from typing import Dict, List


class SeedanceModels(Enum):
    """Seedance模型枚举"""
    PRO = "doubao-seedance-1-0-pro-250528"
    LITE_T2V = "doubao-seedance-1-0-lite-t2v-250428"
    LITE_I2V = "doubao-seedance-1-0-lite-i2v-250428"


class TaskStatus(Enum):
    """任务状态枚举"""
    PENDING = "pending"
    PROCESSING = "processing"
    RUNNING = "running"
    SUCCEEDED = "succeeded"
    FAILED = "failed"
    CANCELLED = "cancelled"

    @classmethod
    def is_terminal(cls, status: str) -> bool:
        """判断是否为终态"""
        return status in [cls.SUCCEEDED.value, cls.FAILED.value, cls.CANCELLED.value]

    @classmethod
    def is_active(cls, status: str) -> bool:
        """判断是否为活跃状态"""
        return status in [cls.PENDING.value, cls.PROCESSING.value, cls.RUNNING.value]


