from PyQt5.QtCore import Qt, QAbstractListModel, QModelIndex, QVariant, pyqtSignal, pyqtSlot
from PyQt5.QtGui import QPixmap
from typing import List, Dict, Any, Optional, Callable
from utils.task_runner import task_runner
from utils.thumbnail_pool import thumbnail_pool
from collections import OrderedDict
import os
from utils.ui_utils import DpiScaler


class ImageHistoryModel(QAbstractListModel):
    RecordRole = Qt.UserRole + 1
    PixmapRole = Qt.UserRole + 2
    _pixmap_ready = pyqtSignal(int)

    def __init__(self, parent=None):
        super().__init__(parent)
        self._items: List[Dict[str, Any]] = []
        self._pix_cache: "OrderedDict[str, QPixmap]" = OrderedDict()
        # LRU 容量可通过环境变量配置
        try:
            self._pix_capacity = int(os.getenv('GEMENG_LRU_PIXMAPS', '256') or '256')
        except Exception:
            self._pix_capacity = 256
        self._failed_paths: Dict[str, int] = {}
        # 列宽真源（由视图下发），用于缩略图目标宽度与 sizeHint 计算（委托端）
        try:
            self._target_width = DpiScaler.px(280)
        except Exception:
            self._target_width = 280

        try:
            self._pixmap_ready.connect(self._apply_pixmap_ready, Qt.QueuedConnection)
        except Exception:
            pass

    def set_column_width(self, width: int):
        try:
            width = int(width)
            if width <= 0 or width == getattr(self, '_target_width', 0):
                return
            self._target_width = width
            # 列宽变更会影响所有项的 sizeHint 与缩略图目标宽度
            self.layoutAboutToBeChanged.emit()
            self.layoutChanged.emit()
            if self.rowCount() > 0:
                top = self.index(0)
                bottom = self.index(self.rowCount() - 1)
                try:
                    self.dataChanged.emit(top, bottom, [Qt.SizeHintRole])
                except Exception:
                    pass
        except Exception:
            pass

    def get_target_width(self) -> int:
        try:
            return int(self._target_width)
        except Exception:
            return 280

    def preheat_thumbnail(self, path: str, pix: Optional[QPixmap] = None, target_width: Optional[int] = None):
        """预热缩略图到LRU缓存（主线程调用）。"""
        try:
            if not path:
                return
            tw = int(target_width) if target_width else self.get_target_width()
            key = f"{path}@{int(tw)}"
            if key in self._pix_cache:
                # 触顶
                p = self._pix_cache.pop(key)
                self._pix_cache[key] = p
                return
            if pix is None:
                if not os.path.exists(path):
                    return
                p = QPixmap(path)
                if p.isNull():
                    return
                target_h = int(round(tw * p.height() / max(1, p.width())))
                pix = p.scaled(max(1, tw), target_h, Qt.KeepAspectRatio, Qt.SmoothTransformation)
            if pix:
                self._pix_cache[key] = pix
                if len(self._pix_cache) > self._pix_capacity:
                    self._pix_cache.popitem(last=False)
        except Exception:
            pass

    def rowCount(self, parent=QModelIndex()) -> int:
        return 0 if parent.isValid() else len(self._items)

    def data(self, index: QModelIndex, role: int = Qt.DisplayRole):
        if not index.isValid():
            return QVariant()
        i = index.row()
        if i < 0 or i >= len(self._items):
            return QVariant()
        rec = self._items[i]
        if role == Qt.DisplayRole:
            return rec.get('prompt', '')
        if role == self.RecordRole:
            return rec
        if role == self.PixmapRole:
            # 根据路径读取缩略图或原图（异步）
            # 优先级：本地缩略图 -> 缩略图路径 -> 绝对路径 -> 文件路径
            path = rec.get('thumbnail_local_path') or rec.get('thumbnail_path') or rec.get('absolute_path') or rec.get('file_path')
            if not path:
                return QVariant()
            key = f"{path}@{int(self._target_width)}"
            if key in self._pix_cache:
                pix = self._pix_cache.pop(key)
                self._pix_cache[key] = pix  # move to end (MRU)
                return pix

            def _load():
                if not os.path.exists(path):
                    return None
                pix = QPixmap(path)
                if pix.isNull():
                    return None
                # 以列宽为目标宽度进行等比缩放（仅用于显示缓存，不影响高度计算）
                target_w = max(1, int(self._target_width))
                target_h = int(round(target_w * pix.height() / max(1, pix.width())))
                return pix.scaled(target_w, target_h, Qt.KeepAspectRatio, Qt.SmoothTransformation)

            def _on_done(pix):
                if pix:
                    # LRU容量控制
                    if key in self._pix_cache:
                        self._pix_cache.pop(key)
                    self._pix_cache[key] = pix
                    if len(self._pix_cache) > self._pix_capacity:
                        self._pix_cache.popitem(last=False)
                    try:
                        self._pixmap_ready.emit(index.row())
                    except Exception:
                        pass
                else:
                    # 记录失败次数，供 UI 作重试决策
                    self._failed_paths[path] = self._failed_paths.get(path, 0) + 1

            thumbnail_pool.submit(_load, key=f"img-thumb:{path}", on_done=_on_done)
            return QVariant()
        return QVariant()

    def roleNames(self):
        roles = super().roleNames()
        roles[self.RecordRole] = b'record'
        roles[self.PixmapRole] = b'pixmap'
        return roles

    def clear(self):
        if not self._items:
            return
        self.beginResetModel()
        self._items.clear()
        self._pix_cache.clear()
        self._failed_paths.clear()
        self.endResetModel()

    def add_items(self, records: List[Dict[str, Any]]):
        if not records:
            return
        start = len(self._items)
        self.beginInsertRows(QModelIndex(), start, start + len(records) - 1)
        self._items.extend(records)
        self.endInsertRows()

    def prepend_items(self, records: List[Dict[str, Any]]):
        """将新记录插入到顶部，避免重复(file_id/file_path)"""
        if not records:
            return
        try:
            # 构建已存在的ID集合
