# -*- coding: utf-8 -*-

from PyQt5.QtWidgets import QWidget, QVBoxLayout, QListView
from PyQt5.QtCore import Qt, pyqtSignal, QTimer
from components.models.image_history_model import ImageHistoryModel
from components.delegates.image_card_delegate import ImageCardDelegate
from utils.ui_utils import StyleManager, DpiScaler, compute_card_layout
from utils.task_runner import task_runner
from typing import Callable, Optional, List, Dict, Any


class ImageHistoryView(QWidget):
    """图片历史虚拟化视图（灰度开关使用）"""

    # 主线程更新信号（跨线程 on_done 仅 emit，不直接改模型）
    items_appended = pyqtSignal(list)
    items_prepended = pyqtSignal(list)
    update_status = pyqtSignal(str)

    def __init__(self, parent=None, style: StyleManager = None):
        super().__init__(parent)
        self.style = style or StyleManager('dark')
        self.model = ImageHistoryModel(self)
        self.list = QListView(self)
        self.list.setViewMode(QListView.IconMode)
        self.list.setResizeMode(QListView.Adjust)
        # 统一开启非统一尺寸项（按原比例显示）
        self.list.setUniformItemSizes(False)
        self.list.setSpacing(DpiScaler.px(10))
        self.list.setWrapping(True)
        self.list.setMovement(QListView.Static)
        self.list.setModel(self.model)
        self._delegate = ImageCardDelegate(self.list, self.style)
        self.list.setItemDelegate(self._delegate)
        self.list.setVerticalScrollMode(QListView.ScrollPerPixel)
        self.list.setMouseTracking(True)
        self.list.verticalScrollBar().valueChanged.connect(self._on_scroll)
        self._status_cb: Optional[Callable[[str], None]] = None
        self._click_cb: Optional[Callable[[dict], None]] = None
        self.list.clicked.connect(self._on_clicked)

        layout = QVBoxLayout(self)
        layout.setContentsMargins(0, 0, 0, 0)
        layout.addWidget(self.list)

        self._repo = None
        # 参数可配置（已在模块顶层导入 os，避免函数内再次导入导致作用域冲突）
        try:
            self._page_size = int(os.getenv('GEMENG_PAGE_SIZE', '40') or '40')
            self._prefetch_threshold = float(os.getenv('GEMENG_PREFETCH_THRESHOLD', '0.9') or '0.9')
        except Exception:
            self._page_size = 40
            self._prefetch_threshold = 0.9
        self._next_cursor = None
        self._loading = False

        # 计算列宽真源并下发
        self._apply_column_width(initial=True)

        # 连接信号以保证主线程更新
        try:
            self.items_appended.connect(self._apply_add_items)
            self.items_prepended.connect(self._apply_prepend_items)
            self.update_status.connect(self._apply_update_status)
        except Exception:
            pass

    def _apply_add_items(self, items: list):
        try:
            if items:
                self.model.add_items(items)
        except Exception:
            pass

    def _apply_prepend_items(self, items: list):
        try:
            if items:
                self.model.prepend_items(items)
        except Exception:
            pass

    def _apply_update_status(self, text: str):
        try:
            if self._status_cb and isinstance(text, str):
                self._status_cb(text)
        except Exception:
            pass

    def _existing_ids(self) -> set:
        ids = set()
        try:
            for i in range(self.model.rowCount()):
                rec = self.model.get_record(i) or {}
                fid = rec.get('file_id') or rec.get('id') or rec.get('file_path')
                if fid:
                    ids.add(str(fid))
        except Exception:
            pass
        return ids

    def prepend_latest(self, page_size: int = 40):
        if self._loading or not self._repo:
            return
        self._loading = True
        exist = self._existing_ids()

        def _task():
            # 使用批量接口从最新开始取
            return self._repo.get_records_batch(0, page_size, 'image')

        def _on_done(items: List[Dict[str, Any]]):
            try:
                new_items = []
                for r in (items or []):
                    fid = r.get('file_id') or r.get('id') or r.get('file_path')
                    if fid and str(fid) not in exist:
                        new_items.append(r)
                if new_items:
                    # 在主线程执行模型更新与状态变更
                    self.items_prepended.emit(new_items)
                    self.update_status.emit(f"新增 {len(new_items)} 张，累计 {self.model.rowCount() + len(new_items)} 张图片")
            finally:
                self._loading = False

        task_runner.submit(_task, key="image-history-view:prepend", on_done=_on_done)

    def _apply_column_width(self, initial: bool = False):
        try:
            vw = self.list.viewport().width()
            if vw <= 0 and initial:
                # 初次可能未布局完成，使用一个保底宽度
                vw = DpiScaler.px(1200)
            layout = compute_card_layout(vw)
            col_w = layout['column_width']
            self.model.set_column_width(col_w)
            self._delegate.set_column_width(col_w)
            # 强制几何更新
            self.list.updateGeometries()
            if self.model.rowCount() > 0:
                self.model.layoutAboutToBeChanged.emit()
                self.model.layoutChanged.emit()
        except Exception:
            pass

    def resizeEvent(self, event):
        super().resizeEvent(event)
