# 导入设置页面组件
from components.settings_page import SettingsPage
from components.forms import SchemaFormRenderer
from components.widgets.reference_assets_panel import ReferenceAssetsPanel
from modules.provider import get_ui_schema


def get_model_display_name(model_code: str) -> str:
    """将模型代码转换为完整的显示名称"""
    model_mapping = {
        'pro': 'Seedance Pro',
        'lite': 'Seedance Lite',
        'doubao-seedance-1-0-pro': 'Seedance Pro',
        'doubao-seedance-1-0-lite': 'Seedance Lite',
        'doubao-seedance-1-0-pro-250528': 'Seedance Pro',
        'doubao-seedance-1-0-lite-250528': 'Seedance Lite',
        # 添加更多模型映射
        'glm-4v': 'GLM-4V',
        'glm-4': 'GLM-4',
        'glm-4-plus': 'GLM-4 Plus',
        'cogview-3': 'CogView-3',
        'cogview-3-plus': 'CogView-3 Plus'
    }

    # 如果找到精确匹配，返回映射的名称
    if model_code in model_mapping:
        return model_mapping[model_code]

    # 如果没有精确匹配，尝试部分匹配
    model_lower = model_code.lower()
    if 'pro' in model_lower:
        return 'Seedance Pro'
    elif 'lite' in model_lower:
        return 'Seedance Lite'
    elif 'glm' in model_lower:
        return f'GLM-{model_code.split("-")[-1].upper()}' if '-' in model_code else 'GLM'
    elif 'cogview' in model_lower:
        return 'CogView'

    # 如果都没有匹配，返回原始代码的首字母大写形式
    return model_code.replace('_', ' ').replace('-', ' ').title() if model_code else '未知模型'

# ===== P0b UI 兜底：显示辅助 =====
def _parse_parameters_field(rec: Dict[str, Any]) -> Dict[str, Any]:
    params = rec.get('parameters', {})
    if isinstance(params, str):
        try:
            return json.loads(params) or {}
        except Exception:
            return {}
    return params or {}

def build_resolution_text(rec: Dict[str, Any]) -> str:
    """优先显示 '分辨率档位 + 比例'，否则回退为 'widthxheight'，再否则回退原resolution/未知"""
    try:
        params = _parse_parameters_field(rec)
        rg = params.get('resolution_grade') or rec.get('resolution_grade')
        rt = params.get('ratio') or rec.get('ratio')
        if rg and rt:
            return f"{str(rg)} {str(rt)}"
        if rg:
            return str(rg)
        # 回退 width×height
        width = rec.get('width') or params.get('width')
        height = rec.get('height') or params.get('height')
        # 有些记录 width/height 可能是字符串或浮点
        def _to_int(v):
            try:
                return int(float(v))
            except Exception:
                return None
        w = _to_int(width)
        h = _to_int(height)
        if w and h:
            return f"{w}x{h}"
        # 最后回退已有的 resolution 文本（如果有）
        res = rec.get('resolution')
        if isinstance(res, str) and res.strip():
            return res
        return '未知'
    except Exception:
        return '未知'

def pick_model_code(rec: Dict[str, Any]) -> str:
    """优先顶层 model；否则从 parameters.model/model_id/model_type/mode 推断"""
    try:
        top = rec.get('model')
        if top:
            return str(top)
        params = _parse_parameters_field(rec)
        cand = params.get('model') or params.get('model_id')
        if cand:
            return str(cand)
        # model_type: 'pro' | 'lite'
        mt = params.get('model_type') or rec.get('model_type')
        if isinstance(mt, str) and mt.strip():
            return mt.strip()
        # 从 mode 文本中粗略识别（例如“文生视频(Pro)”）
