    def download_image(self):
        """下载图片到指定位置"""
        from PyQt5.QtWidgets import QFileDialog, QMessageBox
        import shutil

        image_path = self.image_data.get('absolute_path', '') or self.image_data.get('file_path', '')
        if not image_path or not os.path.exists(image_path):
            self.show_styled_message("错误", "图片文件不存在", "warning")
            return

        # 获取原始文件名
        original_filename = os.path.basename(image_path)

        # 打开保存对话框
        save_path, _ = QFileDialog.getSaveFileName(
            self,
            "保存图片",
            original_filename,
            "图片文件 (*.png *.jpg *.jpeg *.bmp *.gif)"
        )

        if save_path:
            try:
                shutil.copy2(image_path, save_path)
                self.show_styled_message("成功", f"图片已保存到:\n{save_path}", "information")
            except Exception as e:
                self.show_styled_message("错误", f"保存图片失败:\n{str(e)}", "critical")

