    def _handle_field_change(self, field_name: str):
        field = self._fields.get(field_name)
        getter = self._getters.get(field_name)
        if not field or not getter:
            return

        value = getter()
        self._value_cache[field_name] = value
        self.fieldValueChanged.emit(field_name, value)

        if field.visible_if or field.enabled_if:
            self._refresh_dependencies()

        if field.extra.get("reload_on_change"):
            option = None
            if field_name in self._option_meta:
                option = self._option_meta[field_name].get(value)
            if option and option.meta.get("ui_variant"):
                self.schemaReloadRequested.emit(str(option.meta["ui_variant"]))
            else:
                self.schemaReloadRequested.emit(str(value))

    def _refresh_dependencies(self):
        for controller, dependents in self._dependency_index.items():
            controller_value = self.current_value(controller)
            for name in dependents:
                field = self._fields.get(name)
                widget = self._widgets.get(name)
                label = self._labels.get(name)
                if not field or widget is None or label is None:
                    continue

                visible = self._evaluate_visibility(field, controller_value)
                widget.setVisible(visible)
                label.setVisible(visible)
                container = self._containers.get(name)
                if container is not None:
                    container.setVisible(visible)

                enabled = self._evaluate_enabled(field, controller_value)
                widget.setEnabled(enabled)

    def _evaluate_visibility(self, field: UISchemaField, _) -> bool:
        if not field.visible_if:
            return True
        for dep in field.visible_if:
            value = self.current_value(dep.field)
            if dep.exists and not value:
                return False
            if dep.equals is not None and value != dep.equals:
                return False
            if dep.not_equals is not None and value == dep.not_equals:
                return False
        return True

    def _evaluate_enabled(self, field: UISchemaField, _) -> bool:
        if not field.enabled_if:
            return True
        for dep in field.enabled_if:
            value = self.current_value(dep.field)
            if dep.exists and not value:
                return False
            if dep.equals is not None and value != dep.equals:
                return False
            if dep.not_equals is not None and value == dep.not_equals:
                return False
        return True
