    def _calculate_from_ratio(self, width: Optional[int] = None, height: Optional[int] = None, anchor: str = 'width') -> None:
        if not (self.ratio_combo and self.width_spinbox and self.height_spinbox):
            return
        ratio_value = self.ratio_combo.currentData()
        if not ratio_value or ':' not in ratio_value:
            return
        width_ratio, height_ratio = map(float, ratio_value.split(':'))
        if anchor == 'width':
            base_width = width if width is not None else self.width_spinbox.value()
            new_height = int(base_width * height_ratio / width_ratio)
            new_height = max(512, min(2048, new_height))
            self.height_spinbox.blockSignals(True)
            self.height_spinbox.setValue(new_height)
            self.height_spinbox.blockSignals(False)
        else:
            base_height = height if height is not None else self.height_spinbox.value()
            new_width = int(base_height * width_ratio / height_ratio)
            new_width = max(512, min(2048, new_width))
            self.width_spinbox.blockSignals(True)
            self.width_spinbox.setValue(new_width)
            self.width_spinbox.blockSignals(False)

    def _update_ratio_preview(self) -> None:
        if not (self.ratio_display_label and self.width_spinbox and self.height_spinbox):
            return
        width = self.width_spinbox.value()
        height = self.height_spinbox.value()
        if height <= 0:
            self.ratio_display_label.setText("-")
            return
        ratio_value = width / height
        for target, label in [
            (16 / 9, "16:9"),
            (9 / 16, "9:16"),
            (4 / 3, "4:3"),
            (3 / 4, "3:4"),
            (1.0, "1:1"),
            (3 / 2, "3:2"),
            (2 / 3, "2:3"),
            (21 / 9, "21:9"),
        ]:
            if abs(ratio_value - target) < 0.02:
                self.ratio_display_label.setText(label)
                break
        else:
            self.ratio_display_label.setText(f"{ratio_value:.2f}")

    def _sync_custom_size_inputs(self, size_value: str) -> None:
        if not (self.width_spinbox and self.height_spinbox):
            return
        if 'x' not in size_value:
            return
        try:
            width_str, height_str = size_value.split('x', 1)
            width = int(width_str)
            height = int(height_str)
        except Exception:
            return
        self.width_spinbox.blockSignals(True)
        self.height_spinbox.blockSignals(True)
        self.width_spinbox.setValue(width)
        self.height_spinbox.setValue(height)
        self.width_spinbox.blockSignals(False)
        self.height_spinbox.blockSignals(False)
        self._update_ratio_preview()

