                option = self._option_meta[field_name].get(value)
            if option and option.meta.get("ui_variant"):
                self.schemaReloadRequested.emit(str(option.meta["ui_variant"]))
            else:
                self.schemaReloadRequested.emit(str(value))

    def _refresh_dependencies(self):
        for controller, dependents in self._dependency_index.items():
            controller_value = self.current_value(controller)
            for name in dependents:
                field = self._fields.get(name)
                widget = self._widgets.get(name)
                label = self._labels.get(name)
                if not field or widget is None or label is None:
                    continue

                visible = self._evaluate_visibility(field, controller_value)
                widget.setVisible(visible)
                label.setVisible(visible)
                container = self._containers.get(name)
                if container is not None:
                    container.setVisible(visible)
