{"output":"def summarize_reference_assets(record: Dict[str, Any], *, max_preview: int = 5) -> Dict[str, Any]:
    """Return a sanitized summary for UI usage.

    Result structure:
        {
            'count': int,
            'summary': str,
            'labels': List[str],
            'assets': List[Dict[str, Any]],
        }
    """

    assets = collect_reference_assets(record)
    count = len(assets)

    labels: List[str] = []
    for idx, asset in enumerate(assets):
        order = asset.get("order")
        if isinstance(order, (int, float)):
            ordinal = int(order) + 1
        else:
            ordinal = idx + 1
        if ordinal < 1:
            ordinal = idx + 1

        label = f"#{ordinal:02d}"
        kind = asset.get("kind")
        if isinstance(kind, str) and kind.strip():
            label = f"{label}({kind.strip()})"
        labels.append(label)

    if count:
        preview = "ã€".join(labels[:max_preview])
        summary = f"å‚è€ƒå›¾ {count} å¼ ï¼š{preview}"
        if count > max_preview:
            summary += "..."
    else:
        summary = ""

    return {
        "count": count,
        "summary": summary,
        "labels": labels,
        "assets": assets,
    }

