            def _build_reference_assets(self) -> List[Dict[str, Any]]:
                assets: List[Dict[str, Any]] = []

                def append_path(path: Optional[str], kind: Optional[str] = None):
                    if not path:
                        return
                    file_path = Path(path)
                    asset = {
                        'path': str(file_path),
                        'order': len(assets),
                        'source': file_path.name,
                        'kind': kind or 'image',
                        'auto_compress': True,
                    }
                    try:
                        asset['size'] = file_path.stat().st_size
                    except OSError:
                        pass
                    assets.append(asset)

                append_path(self.first_frame, 'first_frame')
                append_path(self.last_frame, 'last_frame')

                extras = self.reference_images or []
                for item in extras:
                    if isinstance(item, dict):
                        asset = dict(item)
                    elif isinstance(item, str):
                        file_path = Path(item)
                        asset = {
                            'path': str(file_path),
                            'source': file_path.name,
                        }
                    else:
                        continue

                    path_value = asset.get('path')
                    if not path_value:
                        continue

                    if not asset.get('source'):
                        asset['source'] = Path(path_value).name
                    asset['order'] = len(assets)
                    asset.setdefault('kind', 'image')
                    asset.setdefault('auto_compress', True)
                    assets.append(asset)

                return assets
