class VideoHistoryModel(QAbstractListModel):
    RecordRole = Qt.UserRole + 1
    PixmapRole = Qt.UserRole + 2
    _pixmap_ready = pyqtSignal(int)

    def __init__(self, parent=None):
        super().__init__(parent)
        self._items: List[Dict[str, Any]] = []
        self._pix_cache: "OrderedDict[str, QPixmap]" = OrderedDict()
        try:
            self._pix_capacity = int(os.getenv('GEMENG_LRU_PIXMAPS', '256') or '256')
        except Exception:
            self._pix_capacity = 256
        try:
            self._target_width = DpiScaler.px(280)
        except Exception:
            self._target_width = 280
        self._failed_paths: Dict[str, int] = {}

        try:
            self._pixmap_ready.connect(self._apply_pixmap_ready, Qt.QueuedConnection)
        except Exception:
            pass

    @staticmethod
    def _prepare_record(record: Dict[str, Any]) -> Dict[str, Any]:
        if not isinstance(record, dict):
            return {}
        info = summarize_reference_assets(record)
        record['_reference_info'] = info
        return record

    @staticmethod
    def _merge_dict(target: Dict[str, Any], source: Dict[str, Any]) -> bool:
        """æµ…åˆå¹¶å­—å…¸ï¼ˆå¿½ç•¥ç©ºå€¼ï¼‰ï¼Œè¿”å›æ˜¯å¦æœ‰å˜æ›´"""
        if not isinstance(source, dict):
            return False
        changed = False
        for key, value in source.items():
            if value in (None, '', [], {}):
                continue
            if not isinstance(target, dict):
                target = {}
            old = target.get(key)
            if old != value:
                target[key] = value
                changed = True
        return changed

    def merge_record(self, record: Dict[str, Any]) -> bool:
        """æ ¹æ® file_id/generation_id æ›´æ–°å·²æœ‰è®°å½•ï¼Œè¿”å›æ˜¯å¦å‘ç”Ÿå˜åŒ–"""
        if not isinstance(record, dict):
            return False

        fid = record.get('file_id') or record.get('generation_id') or record.get('id')
        if not fid:
            return False
        fid = str(fid)

        for idx, existing in enumerate(self._items):
            existing_fid = existing.get('file_id') or existing.get('generation_id') or existing.get('id')
            if existing_fid and str(existing_fid) == fid:
                changed = False
                for key, value in record.items():
                    if key in {'_resolved_reference_assets', '_resolved_reference_assets_abs'}:
                        continue
                    if value in (None, '', [], {}):
                        continue

                    if key in {'metadata', 'parameters', 'batch_info'}:
                        sub = existing.get(key)
                        if not isinstance(sub, dict):
                            sub = {}
                        if self._merge_dict(sub, value):
                            existing[key] = sub
                            changed = True
                        continue

                    if existing.get(key) != value:
                        existing[key] = value
                        changed = True

                if changed:
                    self._items[idx] = self._prepare_record(existing)
                    idx_q = self.index(idx)
                    roles = [self.RecordRole, Qt.DisplayRole, self.PixmapRole, Qt.ToolTipRole]
                    try:
                        self.dataChanged.emit(idx_q, idx_q, roles)
                    except Exception:
                        self.dataChanged.emit(idx_q, idx_q)
                return changed
        return False

    @staticmethod
    def _ensure_reference_info(record: Dict[str, Any]) -> Dict[str, Any]:
        info = record.get('_reference_info') if isinstance(record, dict) else None
        if not isinstance(info, dict):
