class UnifiedStyleSystem(QObject):
    """ç»Ÿä¸€çš„æ ·å¼ç®¡ç†ç³»ç»Ÿ - å¢å¼ºç‰ˆ

    è´Ÿè´£ç®¡ç†æ‰€æœ‰UIç»„ä»¶çš„æ ·å¼ï¼Œç¡®ä¿å­—ä½“å¤§å°å¯ä»¥ç»Ÿä¸€ç¼©æ”¾
    æ”¯æŒç»„ä»¶ç±»å‹è‡ªåŠ¨è¯†åˆ«å’Œæ ·å¼ç»§æ‰¿
    """

    styleChanged = pyqtSignal()

    _instance: Optional['UnifiedStyleSystem'] = None

    def __new__(cls):
        if cls._instance is None:
            cls._instance = super().__new__(cls)
        return cls._instance

    def __init__(self):
        # å…ˆè°ƒç”¨çˆ¶ç±»åˆå§‹åŒ–
        super().__init__()

        # æ£€æŸ¥æ˜¯å¦å·²ç»åˆå§‹åŒ–ï¼ˆå•ä¾‹æ¨¡å¼ï¼‰
        if hasattr(self, '_initialized') and self._initialized:
            return

        self._initialized = True
        self._base_font_size = 12
        self._font_scale = 1.0
        self._style_cache: Dict[str, str] = {}
        self._style_manager = None  # å°†åœ¨åˆå§‹åŒ–æ—¶è®¾ç½®
        self._widget_registry: Dict[int, str] = {}  # è®°å½•widget IDåˆ°ç±»å‹çš„æ˜ å°„

        # ç»„ä»¶ç±»å‹æ˜ å°„è¡¨
        self._widget_type_map = {
            QLabel: "QLabel",
            QComboBox: "QComboBox",
            QPushButton: "QPushButton",
            QSpinBox: "QSpinBox",
            QDoubleSpinBox: "QDoubleSpinBox",
            QSlider: "QSlider",
            QCheckBox: "QCheckBox",
            QRadioButton: "QRadioButton",
            QGroupBox: "QGroupBox",
            QTextEdit: "QTextEdit",
            QLineEdit: "QLineEdit",
            QScrollArea: "QScrollArea",
            QProgressBar: "QProgressBar",
            QListWidget: "QListWidget",
            QListView: "QListView",
            QTableWidget: "QTableWidget",
            QTableView: "QTableView",
            QTreeWidget: "QTreeWidget",
            QTreeView: "QTreeView",
            QTabWidget: "QTabWidget",
            QToolButton: "QToolButton",
            QMenuBar: "QMenuBar",
            QMenu: "QMenu",
            QToolBar: "QToolBar"
        }

        # å°è¯•è·å–å…¨å±€æ ·å¼ç®¡ç†å™¨
        try:
            from utils.ui_utils import StyleManager
            self._style_manager = StyleManager()
        except Exception:
            pass

    def identify_widget_type(self, widget: QWidget) -> str:
        """æ™ºèƒ½è¯†åˆ«ç»„ä»¶ç±»å‹"""
        # é¦–å…ˆæ£€æŸ¥æ˜¯å¦æœ‰æ³¨å†Œçš„ç±»å‹
        widget_id = id(widget)
        if widget_id in self._widget_registry:
            return self._widget_registry[widget_id]

        # é€šè¿‡ç±»å‹æ˜ å°„è¡¨è¯†åˆ«
        for widget_class, type_name in self._widget_type_map.items():
            if isinstance(widget, widget_class):
                self._widget_registry[widget_id] = type_name
                return type_name

        # å¦‚æœéƒ½ä¸åŒ¹é…ï¼Œè¿”å›ç±»å
        class_name = widget.__class__.__name__
        self._widget_registry[widget_id] = class_name
        return class_name

    def set_style_manager(self, style_manager):
        """è®¾ç½®æ ·å¼ç®¡ç†å™¨å¼•ç”¨"""
        self._style_manager = style_manager

    def set_font_scale(self, scale: float):
        """è®¾ç½®å­—ä½“ç¼©æ”¾æ¯”ä¾‹"""
        self._font_scale = scale
        self._style_cache.clear()
        self.styleChanged.emit()

    def get_scaled_font_size(self, base_size: int = None) -> int:
        """è·å–ç¼©æ”¾åçš„å­—ä½“å¤§å°"""
        base = base_size or self._base_font_size
        scaled = int(round(base * self._font_scale))
        return max(8, scaled)  # ç¡®ä¿æœ€å°å­—ä½“å¤§å°ä¸º8

    def get_colors(self) -> Dict[str, str]:
        """è·å–é¢œè‰²é…ç½®"""
        if self._style_manager:
            return {
                'text': self._style_manager.get_color('text'),
                'surface': self._style_manager.get_color('surface'),
                'border': self._style_manager.get_color('border'),
                'primary': self._style_manager.get_color('primary'),
                'primary_hover': '#0d7377dd',  # ä½¿ç”¨å›ºå®šå€¼ï¼Œé¿å…è°ƒç”¨é”™è¯¯
                'primary_pressed': '#0d7377aa',  # ä½¿ç”¨å›ºå®šå€¼ï¼Œé¿å…è°ƒç”¨é”™è¯¯
                'surface_hover': '#2d2d2ddd',  # ä½¿ç”¨å›ºå®šå€¼ï¼Œé¿å…è°ƒç”¨é”™è¯¯
                'background': '#1e1e1e'  # ä½¿ç”¨å›ºå®šå€¼
            }
        else:
            return {
                'text': '#e4e4e4',
                'surface': '#2d2d2d',
                'border': '#3a3a3a',
                'primary': '#0d7377',
                'primary_hover': '#0d7377dd',
                'primary_pressed': '#0d7377aa',
                'surface_hover': '#2d2d2ddd',
                'background': '#1e1e1e'
            }

    def get_widget_style(self, widget_type: str, extra: Dict[str, Any] = None) -> str:
        """è·å–ç»„ä»¶æ ·å¼"""
        cache_key = f"{widget_type}_{self._font_scale}"
        if extra:
