# -*- coding: utf-8 -*-
"""Utility helpers for handling reference assets metadata."""

from __future__ import annotations

import json
from typing import Any, Dict, Iterable, List, Sequence, Tuple

__all__ = [
    "collect_reference_assets",
    "summarize_reference_assets",
]


def _ensure_dict(value: Any) -> Dict[str, Any]:
    if isinstance(value, dict):
        return value
    if isinstance(value, str):
        try:
            parsed = json.loads(value)
            if isinstance(parsed, dict):
                return parsed
        except json.JSONDecodeError:
            return {}
    return {}


def _ensure_sequence(value: Any) -> Sequence[Any]:
    if isinstance(value, (list, tuple)):
        return value  # type: ignore[return-value]
    return []


def _normalize_asset(item: Any, index: int) -> Dict[str, Any] | None:
    if not isinstance(item, dict):
        return None

    normalized: Dict[str, Any] = {}

    asset_id = item.get("id")
    if asset_id not in (None, ""):
        normalized["id"] = str(asset_id)

    order_raw = item.get("order")
    if isinstance(order_raw, (int, float)):
        order = int(order_raw)
    else:
        try:
            order = int(str(order_raw))
        except Exception:
            order = index
    if order < 0:
        order = index
    normalized["order"] = order

    kind = item.get("kind") or item.get("type")
    if isinstance(kind, str):
        kind = kind.strip()
        if kind:
            normalized["kind"] = kind
    content_type = item.get("type") or item.get("kind")
    if isinstance(content_type, str):
        content_type = content_type.strip()
        if content_type:
            normalized["type"] = content_type

    for key in ("path", "relative_path", "absolute_path", "checksum", "width", "height", "size", "source"):
        value = item.get(key)
        if value not in (None, ""):
            normalized[key] = value

    normalized["__source_index"] = index
    return normalized


def collect_reference_assets(record: Dict[str, Any]) -> List[Dict[str, Any]]:
    """Extract reference asset entries from a history record.

    Preference order: `_resolved_reference_assets` -> `reference_assets` ->
    `parameters['reference_assets']`. Returned list is sorted by `order`
    (ascending) with stable fallback to original position. Returned dicts do
    not modify the original record.
    """

    if not isinstance(record, dict):
        return []

    candidates: Iterable[Any]
    for key in ("_resolved_reference_assets", "reference_assets"):
        value = record.get(key)
        if value:
            candidates = _ensure_sequence(value)
            break
    else:
        params = record.get("parameters")
        params_dict = _ensure_dict(params)
        candidates = _ensure_sequence(params_dict.get("reference_assets"))

    normalized: List[Tuple[int, int, Dict[str, Any]]] = []
    for idx, item in enumerate(candidates):
        normalized_item = _normalize_asset(item, idx)
        if normalized_item is None:
            continue
        order = normalized_item.get("order", idx)
        source_idx = normalized_item.pop("__source_index", idx)
        normalized.append((order, source_idx, normalized_item))

    normalized.sort(key=lambda comp: (comp[0], comp[1]))
    return [entry[2] for entry in normalized]


def summarize_reference_assets(record: Dict[str, Any], *, max_preview: int = 5) -> Dict[str, Any]:
    """Return a sanitized summary for UI usage.

    Result structure:
        {
            'count': int,
            'summary': str,
            'labels': List[str],
            'assets': List[Dict[str, Any]],
        }
    """

    assets = collect_reference_assets(record)
    count = len(assets)

    labels: List[str] = []
    for idx, asset in enumerate(assets):
        order = asset.get("order")
        if isinstance(order, (int, float)):
            ordinal = int(order) + 1
        else:
            ordinal = idx + 1
        if ordinal < 1:
            ordinal = idx + 1

        label = f"#{ordinal:02d}"
        kind = asset.get("kind")
        if isinstance(kind, str) and kind.strip():
            label = f"{label}({kind.strip()})"
        labels.append(label)

    if count:
        preview = "„ÄÅ".join(labels[:max_preview])
        summary = f"ÂèÇËÄÉÂõæ {count} Âº†Ôºö{preview}"
        if count > max_preview:
            summary += "..."
    else:
        summary = ""

    return {
        "count": count,
        "summary": summary,
        "labels": labels,
        "assets": assets,
    }
