    def _build_reference_payloads(self, assets: List[Dict[str, Any]]) -> List[str]:
        payloads: List[str] = []
        for asset in assets or []:
            payload = asset.get('_payload_b64')
            if not payload:
                continue
            path_hint = asset.get('path') or asset.get('_source_meta', {}).get('source_path')
            mime = 'image/png'
            try:
                if path_hint:
                    suffix = Path(path_hint).suffix.lower()
                    if suffix in {'.jpg', '.jpeg'}:
                        mime = 'image/jpeg'
                    elif suffix == '.webp':
                        mime = 'image/webp'
                    elif suffix == '.bmp':
                        mime = 'image/bmp'
                    elif suffix == '.gif':
                        mime = 'image/gif'
            except Exception:
                pass
            payloads.append(f"data:{mime};base64,{payload}")
        return payloads

