    def _resolve_video_reference_assets(self, record: Dict[str, Any]) -> List[Dict[str, Any]]:
        """
        Ëß£ÊûêËßÜÈ¢ëËÆ∞ÂΩï‰∏≠ÁöÑÂèÇËÄÉÂõæËµÑ‰∫ß

        ‰ªéparameters.first_frameÂíåimage_pathÊèêÂèñÂèÇËÄÉÂõæ‰ø°ÊÅØ
        """
        if not isinstance(record, dict):
            return []

        params = record.get('parameters', {})
        if isinstance(params, str):
            try:
                params = json.loads(params)
            except:
                params = {}

        # Êî∂ÈõÜÂèÇËÄÉÂõæË∑ØÂæÑ
        ref_paths = []

        # ‰ªéfirst_frameÊèêÂèñ
        first_frame = params.get('first_frame')
        if first_frame and isinstance(first_frame, str) and os.path.exists(first_frame):
            ref_paths.append(first_frame)

        # ‰ªéimage_pathÊèêÂèñ
        image_path = params.get('image_path')
        if image_path and isinstance(image_path, str) and os.path.exists(image_path):
            if image_path not in ref_paths:
                ref_paths.append(image_path)

        # ÊûÑÂª∫ÂèÇËÄÉÂõæËµÑ‰∫ß‰ø°ÊÅØ
        resolved_assets = []
        for i, path in enumerate(ref_paths):
            try:
                file_path = Path(path)
                if file_path.exists():
                    stat = file_path.stat()
                    asset = {
                        'id': f'ref_video_{record.get("generation_id", "unknown")}_{i}',
                        'path': str(path),
                        'absolute_path': str(path),
                        'type': 'image',
                        'kind': 'image',
                        'source': file_path.name,
                        'order': i,
                        'size': stat.st_size,
                        'exists': True,
                        'created_at': record.get('created_at', datetime.now().isoformat())
                    }

                    # Â∞ùËØïËé∑ÂèñÂõæÁâáÂ∞∫ÂØ∏
                    try:
                        from PIL import Image
                        with Image.open(path) as img:
                            asset['width'] = img.width
                            asset['height'] = img.height
                    except:
                        asset['width'] = None
                        asset['height'] = None

                    resolved_assets.append(asset)
            except Exception as e:
                print(f"  ‚ö†Ô∏è Ëß£ÊûêÂèÇËÄÉÂõæÂ§±Ë¥•: {path} - {e}")

        return resolved_assets

