# -*- coding: utf-8 -*-

from PyQt5.QtWidgets import QWidget, QVBoxLayout, QListView
from PyQt5.QtCore import Qt
from components.models.video_history_model import VideoHistoryModel
from components.delegates.video_card_delegate import VideoCardDelegate
from utils.ui_utils import StyleManager, DpiScaler
from utils.task_runner import task_runner
from typing import Callable, Optional
import os


class VideoHistoryView(QWidget):
    """è§†é¢‘å†å²è™šæ‹ŸåŒ–è§†å›¾ï¼ˆç°åº¦å¼€å…³ï¼‰"""

    def __init__(self, parent=None, style: StyleManager = None):
        super().__init__(parent)
        self.style = style or StyleManager('dark')
        self.model = VideoHistoryModel(self)
        self.list = QListView(self)
        self.list.setViewMode(QListView.IconMode)
        self.list.setResizeMode(QListView.Adjust)
        self.list.setUniformItemSizes(True)
        self.list.setSpacing(DpiScaler.px(10))
        self.list.setWrapping(True)
        self.list.setMovement(QListView.Static)
        self.list.setModel(self.model)
        self.list.setItemDelegate(VideoCardDelegate(self.list, self.style))
        self.list.setVerticalScrollMode(QListView.ScrollPerPixel)
        self.list.setMouseTracking(True)
        self.list.verticalScrollBar().valueChanged.connect(self._on_scroll)
        self._status_cb: Optional[Callable[[str], None]] = None
        self._click_cb: Optional[Callable[[dict], None]] = None
        self.list.clicked.connect(self._on_clicked)

        layout = QVBoxLayout(self)
        layout.setContentsMargins(0, 0, 0, 0)
        layout.addWidget(self.list)

        self._repo = None
        try:
            self._page_size = int(os.getenv('GEMENG_PAGE_SIZE', '40') or '40')
            self._prefetch_threshold = float(os.getenv('GEMENG_PREFETCH_THRESHOLD', '0.9') or '0.9')
        except Exception:
            self._page_size = 40
            self._prefetch_threshold = 0.9
        self._next_cursor = None
        self._loading = False

    def set_status_callback(self, cb: Callable[[str], None]):
        self._status_cb = cb

    def set_item_click_callback(self, cb: Callable[[dict], None]):
        self._click_cb = cb

    def bind_repository(self, repo_manager):
        self._repo = repo_manager

    def clear(self):
        self._next_cursor = None
        self.model.clear()

    def load_initial(self):
        if self._loading or not self._repo:
            return
        self._loading = True

        def _task():
            return self._repo.get_records(page_size=self._page_size, cursor=None, record_type='video')

        def _on_done(result):
            try:
                items = result.get('items', []) if isinstance(result, dict) else (result or [])
                self._next_cursor = result.get('next_cursor') if isinstance(result, dict) else None
                self.model.add_items(items)
                if self._status_cb and self._repo:
                    def _count():
                        return self._repo.get_total_count('video')
                    def _on_total(total):
                        self._status_cb(f"å·²åŠ è½½ {self.model.rowCount()} ä¸ªè§†é¢‘ï¼ˆå…± {total} ä¸ªï¼‰")
                    task_runner.submit(_count, key="video-history-view:total", on_done=_on_total)
            finally:
                self._loading = False

        task_runner.submit(_task, key="video-history-view:init", on_done=_on_done)

    def load_next(self):
        if self._loading or not self._repo or not self._next_cursor:
            return
        self._loading = True

        def _task():
            return self._repo.get_records(page_size=self._page_size, cursor=self._next_cursor, record_type='video')

