# -*- coding: utf-8 -*-
"""Schema-driven form renderer for parameter panels."""

from functools import partial
from typing import Any, Callable, Dict, List, Optional, Tuple

from PyQt5.QtCore import Qt, pyqtSignal
from PyQt5.QtWidgets import (
    QCheckBox,
    QComboBox,
    QDoubleSpinBox,
    QFormLayout,
    QHBoxLayout,
    QLabel,
    QLineEdit,
    QSlider,
    QSpinBox,
    QTextEdit,
    QWidget,
)

from modules.provider.schema import UIOption, UISchema, UISchemaField


class SchemaFormRenderer(QWidget):
    """Render Qt widgets based on UISchema definitions."""

    schemaReloadRequested = pyqtSignal(str)
    fieldValueChanged = pyqtSignal(str, object)

    def __init__(self, parent: Optional[QWidget] = None):
        super().__init__(parent)
        self._schema: Optional[UISchema] = None
        self._form_layout = QFormLayout(self)
        self._form_layout.setContentsMargins(0, 0, 0, 0)
        self._form_layout.setHorizontalSpacing(12)
        self._form_layout.setVerticalSpacing(8)

        self._fields: Dict[str, UISchemaField] = {}
        self._widgets: Dict[str, QWidget] = {}
        self._labels: Dict[str, QLabel] = {}
        self._getters: Dict[str, Callable[[], Any]] = {}
        self._setters: Dict[str, Callable[[Any], None]] = {}
        self._option_meta: Dict[str, Dict[Any, UIOption]] = {}
        self._dependency_index: Dict[str, List[str]] = {}
        self._slider_scales: Dict[str, float] = {}
        self._value_cache: Dict[str, Any] = {}

    # ------------------------------------------------------------------
    # Public API
    # ------------------------------------------------------------------

    def set_schema(self, schema: UISchema, initial_values: Optional[Dict[str, Any]] = None):
        """Render schema with optional initial values."""
        self._schema = schema
        self._fields = {field.name: field for field in schema.fields}
        values = initial_values or {}
        self._rebuild(values)

    def collect_values(self) -> Tuple[Dict[str, Any], List[str]]:
        values: Dict[str, Any] = {}
        errors: List[str] = []
        for name, field in self._fields.items():
            getter = self._getters.get(name)
            widget = self._widgets.get(name)
            label = self._labels.get(name)
            if getter is None or widget is None:
                continue

            if not widget.isVisible():
                # éšè—å­—æ®µæ²¿ç”¨å½“å‰å€¼ä½†å…è®¸ä¸ºç©º
                values[name] = getter()
                continue

            value = getter()
            values[name] = value

            if field.required:
                if value is None or (isinstance(value, str) and not value.strip()):
                    label_text = label.text() if label else field.label
                    errors.append(f"å­—æ®µâ€œ{label_text}â€ä¸ºå¿…å¡«é¡¹")
        return values, errors

    def widget(self, name: str) -> Optional[QWidget]:
        """è¿”å›å­—æ®µå¯¹åº”çš„æ§ä»¶ï¼ˆè‹¥å­˜åœ¨ï¼‰ã€‚"""
        return self._widgets.get(name)

    def field_definition(self, name: str) -> Optional[UISchemaField]:
        """è¿”å›å­—æ®µå®šä¹‰ã€‚"""
        return self._fields.get(name)

    def set_field_value(self, name: str, value: Any):
        setter = self._setters.get(name)
        if setter:
            setter(value)
            self._handle_field_change(name)

    def current_value(self, name: str) -> Any:
        getter = self._getters.get(name)
        if getter:
            return getter()
        return None

    def schema(self) -> Optional[UISchema]:
        return self._schema

    # ------------------------------------------------------------------
    # Internal helpers
    # ------------------------------------------------------------------

    def _rebuild(self, initial_values: Dict[str, Any]):
        self._clear_layout()
        self._widgets.clear()
        self._labels.clear()
        self._getters.clear()
        self._setters.clear()
        self._option_meta.clear()
        self._dependency_index.clear()
        self._slider_scales.clear()
        self._value_cache = dict(initial_values)

        if not self._schema:
            return

        for field in self._schema.fields:
            value = initial_values.get(field.name, field.default)
            label_widget = QLabel(field.label)
            label_widget.setObjectName(f"label_{field.name}")
            if field.help_text:
                label_widget.setToolTip(field.help_text)

            widget, getter, setter = self._create_widget(field, value)
            if field.help_text:
                widget.setToolTip(field.help_text)

            self._form_layout.addRow(label_widget, widget)

            self._widgets[field.name] = widget
            self._labels[field.name] = label_widget
            self._getters[field.name] = getter
            self._setters[field.name] = setter

            if field.visible_if or field.enabled_if:
                for dep in field.visible_if + field.enabled_if:
                    self._dependency_index.setdefault(dep.field, []).append(field.name)

        # åˆå§‹ä¾èµ–åˆ·æ–°
        self._refresh_dependencies()

    def _clear_layout(self):
        while self._form_layout.count():
            item = self._form_layout.takeAt(0)
            if not item:
                continue
            widget = item.widget()
            if widget:
                widget.deleteLater()
            else:
                # Row might contain layout (e.g., slider container)
                layout = item.layout()
                if layout:
                    self._delete_layout(layout)

    def _delete_layout(self, layout):
        while layout.count():
            child = layout.takeAt(0)
            widget = child.widget()
            if widget:
                widget.deleteLater()
            elif child.layout():
                self._delete_layout(child.layout())
        layout.deleteLater()

    def _create_widget(self, field: UISchemaField, value: Any) -> Tuple[QWidget, Callable[[], Any], Callable[[Any], None]]:
        field_type = field.field_type.lower()
        if field_type == "select":
            widget = QComboBox()
            option_lookup: Dict[Any, UIOption] = {}
            for option in field.options:
                widget.addItem(option.label, option.value)
                option_lookup[option.value] = option
            self._option_meta[field.name] = option_lookup

            def getter() -> Any:
                return widget.currentData()

            def setter(new_value: Any):
                index = widget.findData(new_value)
                if index >= 0:
                    widget.setCurrentIndex(index)

            widget.currentIndexChanged.connect(partial(self._handle_field_change, field.name))
            setter(value)
            return widget, getter, setter

        if field_type == "toggle":
            checkbox = QCheckBox()
            checkbox.setTristate(False)

