{"output":"#!/usr/bin/env python3
# -*- coding: utf-8 -*-

"""
Electron PoC 本地后端。
提供 /history/image 与 /history/video 接口返回 mock 数据。
"""

from flask import Flask, jsonify, request
import json
import os
from pathlib import Path

BASE_DIR = Path(__file__).resolve().parent.parent.parent  # 项目根目录
DATA_DIR = BASE_DIR / "data"
MOCK_HISTORY_FILE = DATA_DIR / "mock_history_records.json"

app = Flask(__name__)


def _load_records(data_type: str):
    if MOCK_HISTORY_FILE.exists():
        with open(MOCK_HISTORY_FILE, "r", encoding="utf-8") as f:
            payload = json.load(f)
            return payload.get(data_type, [])
    # fallback: 生成简单 mock
    records = []
    for i in range(1, 51 if data_type == "image" else 11):
        records.append({
            "id": f"{data_type}-mock-{i}",
            "prompt": f"{data_type.upper()} Mock Prompt {i}",
            "created_at": "2025-10-09T10:00:00",
            "file_path": f"/mock/{data_type}/{i}.png",
            "parameters": {
                "reference_assets": []
            }
        })
    return records


@app.route("/history/<data_type>")
def history(data_type: str):
    data_type = data_type if data_type in {"image", "video"} else "image"
    page_size = int(request.args.get("pageSize", 40))
    records = _load_records(data_type)[:page_size]
    return jsonify({
        "records": records,
        "page_size": page_size,
        "data_type": data_type
    })


if __name__ == "__main__":
    port = int(os.getenv("ELECTRON_POC_BACKEND_PORT", "5678"))
    app.run(host="127.0.0.1", port=port)
