{"output":"const { app, BrowserWindow, ipcMain } = require('electron');
const path = require('path');
const { performance } = require('perf_hooks');
const fetch = (...args) => import('node-fetch').then(({ default: fetch }) => fetch(...args));

let squirrelStartup = false;
try {
  squirrelStartup = require('electron-squirrel-startup');
} catch (err) {
  squirrelStartup = false;
}
if (squirrelStartup) {
  app.quit();
}

const API_BASE = 'http://127.0.0.1:5678';

const createWindow = () => {
  const mainWindow = new BrowserWindow({
    width: 1000,
    height: 700,
    webPreferences: {
      preload: path.join(__dirname, 'preload.js'),
      contextIsolation: true,
      nodeIntegration: false
    }
  });

  mainWindow.loadFile(path.join(__dirname, 'renderer', 'index.html'));
};

app.whenReady().then(() => {
  ipcMain.handle('history:list', async (_event, { dataType, pageSize }) => {
    const start = performance.now();
    const response = await fetch(`${API_BASE}/history/${dataType}?pageSize=${pageSize || 40}`);
    const mid = performance.now();
    const payload = await response.json();
    const end = performance.now();
    return {
      ...payload,
      timings: {
        fetch: mid - start,
        parse: end - mid,
        total: end - start
      }
    };
  });

  createWindow();

  app.on('activate', () => {
    if (BrowserWindow.getAllWindows().length === 0) {
      createWindow();
    }
  });
});

app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});
