+      try {
+        await window.filesAPI.readText('../package.json');
+        traversalError = 'no_error';
+      } catch (err) {
+        traversalError = err.message || 'error';
+      }
+
+      return {
+        security,
+        listImages,
+        readSample,
+        statInfo,
+        traversalError
+      };
+    }
+  )();`;
