+    timestamp: new Date().toISOString(),
+    rendererData,
+    mainProcessMemory,
+    rendererProcessMemory
+  };
+}
+
+async function runMonitor(win, durationMs, intervalMs) {
+  const samples = [];
+  const startTs = Date.now();
+  const endTs = startTs + durationMs;
+
+  // 初始采集
+  samples.push(await collectSample(win));
+
+  while (Date.now() < endTs) {
+    const waitMs = Math.min(intervalMs, endTs - Date.now());
+    if (waitMs <= 0) {
+      break;
+    }
+    await new Promise(resolve => setTimeout(resolve, waitMs));
+    if (Date.now() >= endTs) {
