+async function runScrollSequence(win) {
+  const script = `(
+    async () => {
+      const waitForStatus = async () => {
+        const deadline = Date.now() + 4000;
+        return new Promise((resolve, reject) => {
+          const check = () => {
+            const status = document.getElementById('status').textContent || '';
+            if (/加载完成/.test(status)) {
+              resolve(status);
+              return;
+            }
+            if (Date.now() > deadline) {
+              reject(new Error('wait status timeout'));
+              return;
+            }
+            setTimeout(check, 100);
+          };
+          check();
+        });
+      };
+
+      const refreshBtn = document.getElementById('refreshBtn');
+      refreshBtn.click();
+      await waitForStatus();
+
+      const recordsBefore = document.querySelectorAll('#grid .card').length;
+
+      const steps = [];
+
+      const performScroll = async (options) => {
+        const { top, label } = options;
+        const start = performance.now();
+        await new Promise((resolve) => {
+          window.scrollTo({ top, behavior: 'smooth' });
+          const check = () => {
+            const current = window.scrollY || document.documentElement.scrollTop;
+            if (Math.abs(current - top) < 5) {
+              resolve();
+              return;
+            }
+            requestAnimationFrame(check);
+          };
+          check();
+        });
+        const end = performance.now();
+        steps.push({ label, durationMs: end - start, finalScroll: window.scrollY || document.documentElement.scrollTop });
+      };
+
+      const totalHeight = document.body.scrollHeight || 0;
+      const viewHeight = window.innerHeight || document.documentElement.clientHeight;
+
+      await performScroll({ label: 'scroll_down', top: totalHeight - viewHeight });
+      await performScroll({ label: 'scroll_up', top: 0 });
+      await performScroll({ label: 'scroll_half', top: Math.floor((totalHeight - viewHeight) / 2) });
+
+      const recordsAfter = document.querySelectorAll('#grid .card').length;
+
+      return {
+        steps,
+        totalHeight,
+        viewHeight,
+        recordsBefore,
+        recordsAfter,
+        status: document.getElementById('status').textContent || '',
+        metrics: document.getElementById('metrics').textContent || ''
+      };
+    }
+  )();`;
+
+  return win.webContents.executeJavaScript(script, true);
+}
+
+async function main() {
+  const win = new BrowserWindow({
+    show: false,
+    webPreferences: {
+      preload: PRELOAD_PATH,
+      contextIsolation: true,
+      nodeIntegration: false
+    }
+  });
+
+  await win.loadFile(INDEX_HTML);
+
+  const result = await runScrollSequence(win);
+  console.log(JSON.stringify(result, null, 2));
+  app.exit(0);
+}
+
+app.whenReady().then(main).catch((err) => {
+  console.error('scroll smoke failed', err);
+  app.exit(1);
+});
