const path = require('path');
const { performance } = require('perf_hooks');
const fs = require('fs/promises');
const fetch = (...args) => import('node-fetch').then(({ default: fetch }) => fetch(...args));

let squirrelStartup = false;
try {
  squirrelStartup = require('electron-squirrel-startup');
} catch (err) {
  squirrelStartup = false;
}
if (squirrelStartup) {
  app.quit();
}

const API_BASE = 'http://127.0.0.1:5678';
const STORAGE_BASE = path.join(__dirname, 'storage');
const STORAGE_SUBDIRS = ['images', 'videos', 'tmp'];
const isHeadless = process.env.ELECTRON_HEADLESS === '1';

const resolveStoragePath = (relativePath = '.') => {
  const target = path.resolve(STORAGE_BASE, relativePath);
  if (!target.startsWith(STORAGE_BASE)) {
    const error = new Error('Access denied');
    error.code = 'E_ACCESS_DENIED';
    throw error;
  }
  return target;
};

const mapDirent = (dirent) => ({
  name: dirent.name,
  type: dirent.isDirectory() ? 'directory' : 'file'
});

async function ensureStorageLayout() {
  await Promise.all(
    STORAGE_SUBDIRS.map((subDir) => fs.mkdir(path.join(STORAGE_BASE, subDir), { recursive: true }))
  );
}

function registerIpcHandlers() {
  ipcMain.handle('history:list', async (_event, { dataType, pageSize }) => {
    const start = performance.now();
    const response = await fetch(`${API_BASE}/history/${dataType}?pageSize=${pageSize || 40}`);
    const mid = performance.now();
    const payload = await response.json();
    const end = performance.now();
    return {
      ...payload,
      timings: {
        fetch: mid - start,
        parse: end - mid,
        total: end - start
      }
    };
  });

  ipcMain.handle('fs:list', async (_event, relativePath = '.') => {
    const target = resolveStoragePath(relativePath);
    const dirents = await fs.readdir(target, { withFileTypes: true });
    return dirents.map(mapDirent);
  });

  ipcMain.handle('fs:readText', async (_event, relativePath) => {
    if (!relativePath) {
      const error = new Error('Path is required');
      error.code = 'E_PATH_REQUIRED';
      throw error;
    }
    const target = resolveStoragePath(relativePath);
    return fs.readFile(target, 'utf8');
  });

  ipcMain.handle('fs:writeText', async (_event, relativePath, contents) => {
    if (!relativePath) {
      const error = new Error('Path is required');
      error.code = 'E_PATH_REQUIRED';
      throw error;
    }
    if (typeof contents !== 'string') {
      const error = new Error('Contents must be string');
      error.code = 'E_CONTENT_TYPE';
      throw error;
    }
    const target = resolveStoragePath(relativePath);
    await fs.mkdir(path.dirname(target), { recursive: true });
    await fs.writeFile(target, contents, 'utf8');
    return { success: true };
  });

  ipcMain.handle('fs:stat', async (_event, relativePath) => {
    if (!relativePath) {
      const error = new Error('Path is required');
      error.code = 'E_PATH_REQUIRED';
      throw error;
    }
    const target = resolveStoragePath(relativePath);
    const stat = await fs.stat(target);
    return {
      size: stat.size,
      modifiedAt: stat.mtimeMs,
      isDirectory: stat.isDirectory()
    };
  });
}

const createWindow = () => {
  const mainWindow = new BrowserWindow({
    width: 1000,
    height: 700,
    webPreferences: {
      preload: path.join(__dirname, 'preload.js'),
      contextIsolation: true,
      nodeIntegration: false
    }
  });

  mainWindow.loadFile(path.join(__dirname, 'renderer', 'index.html'));
};

app.whenReady().then(async () => {
  await ensureStorageLayout();
  registerIpcHandlers();

  if (!isHeadless) {
    createWindow();

    app.on('activate', () => {
      if (BrowserWindow.getAllWindows().length === 0) {
        createWindow();
      }
    });
  }
});

app.on('window-all-closed', () => {
  if (process.platform !== 'darwin') {
    app.quit();
  }
});
