import { McpServer } from "@modelcontextprotocol/sdk/server/mcp.js";
import { StdioServerTransport } from "@modelcontextprotocol/sdk/server/stdio.js";
import { chromium } from "playwright";

// ───────────────────────────────────────────────────────────────────────────────
// 浏览器与页面管理（单例 Browser，按 pageId 管理 Page）
// ───────────────────────────────────────────────────────────────────────────────
const state = {
  browser: /** @type {import('playwright').Browser | null} */ (null),
  pages: new Map(), // pageId -> Page
};

const headless = process.env.PW_HEADLESS !== "0"; // 默认无头；设置 PW_HEADLESS=0 可有头
const defaultViewport = { width: 1280, height: 800 };

/** 生成简单的 pageId */
function newPageId() {
  return `page_${Date.now()}_${Math.floor(Math.random() * 1e6)}`;
}

/** 懒启动 Browser */
async function ensureBrowser() {
  if (!state.browser) {
    state.browser = await chromium.launch({ headless });
  }
  return state.browser;
}

/** 根据 pageId 取 Page */
function getPage(pageId) {
  const page = state.pages.get(pageId);
  if (!page) {
    throw new Error(`Page not found: ${pageId}`);
  }
  return page;
}

/** 关闭并移除 Page */
async function closePage(pageId) {
  const page = state.pages.get(pageId);
  if (page) {
    state.pages.delete(pageId);
    try {
      await page.close({ runBeforeUnload: false });
    } catch {}
  }
}

/** 关闭所有资源（进程退出时调用） */
async function shutdownAll() {
  for (const [id] of state.pages) {
    await closePage(id);
  }
  if (state.browser) {
    await state.browser.close();
    state.browser = null;
  }
}

