server.tool(
  "pw.open",
  {
    title: "Open a new page",
    description:
      "Launch (or reuse) a Chromium instance and open a new page at the given URL. Returns a pageId.",
    inputSchema: {
      type: "object",
      properties: {
        url: { type: "string", description: "URL to open" },
