  args: ["server.mjs"],
  cwd: process.cwd(),
  env: {
    PW_HEADLESS: "1",
    PLAYWRIGHT_BROWSERS_PATH: "0",
  },
  stderr: "pipe",
});

const client = new Client({
  name: "playwright-mcp-connectivity-check",
  version: "0.1.0",
});

