  const listResult = await client.listTools();
  console.log("工具列表:", listResult.tools.map((t) => t.name));
  const openRes = await client.callTool({
    name: "pw.open",
    arguments: { url: "https://example.com" },
  });
  console.log("pw.open 返回:", openRes.structuredContent);
