  if (pageId) {
    const evalRes = await client.callTool({
      name: "pw.eval",
      arguments: { pageId, expression: "document.title" },
    });
    console.log("pw.eval 返回:", evalRes.structuredContent);
    const closeRes = await client.callTool({
      name: "pw.close",
      arguments: { pageId },
    });
    console.log("pw.close 返回:", closeRes.structuredContent ?? closeRes);
  }
} catch (error) {
  console.error("调用失败:", error);
} finally {
  await client.close();
  await transport.close();
}
