{"output":"// server.mjs — Playwright + Electron 的 MCP 服务器（无头 + 兼容 Codex 参数包裹）
// 需要：npm i @modelcontextprotocol/sdk playwright zod
// Node 20+/22 环境

import { McpServer } from '@modelcontextprotocol/sdk/server/mcp.js'
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js'
import { z } from 'zod'
import { chromium, _electron as electron } from 'playwright'

// ---------- 运行配置 ----------
const HEADLESS = process.env.PW_HEADLESS !== '0'           // 默认无头；PW_HEADLESS=0 可有头
const BROWSERS_PATH = process.env.PLAYWRIGHT_BROWSERS_PATH // 可为 "0"（使用 node_modules 安装位置）
if (BROWSERS_PATH) {
  // 仅需存在该环境变量，Playwright 自行处理路径
}

// ---------- MCP 服务器 ----------
const server = new McpServer(
  { name: 'mcp-playwright', version: '1.0.0' },
  { capabilities: { tools: {} } }
)

// ---------- 兼容 Codex 把参数包到 inputSchema 的解包 ----------
const unwrapArgs = (raw) => {
  if (raw && typeof raw === 'object' && raw.inputSchema && typeof raw.inputSchema === 'object') {
    return raw.inputSchema
  }
  return raw ?? {}
}

const parseArgs = (schema) => (raw) => {
  const args = unwrapArgs(raw)
  const res = schema.safeParse(args)
  if (!res.success) {
    console.error('[mcp-playwright] invalid args:', JSON.stringify(args), res.error?.errors)
    throw new Error(
      'Invalid arguments: ' +
        (res.error?.errors?.map((e) => `${e.path?.join('.') || ''} ${e.message}`).join('; ') || 'unknown')
    )
  }
  return res.data
}

// ---------- 单例（browser/page）与清理 ----------
/** @type {import('playwright').Browser|null} */ let browser = null
/** @type {import('playwright').BrowserContext|null} */ let context = null
/** @type {import('playwright').Page|null} */ let page = null

async function ensurePage() {
  if (!browser) {
    browser = await chromium.launch({ headless: HEADLESS })
    context = await browser.newContext({
      locale: 'zh-CN',
      userAgent: 'Mozilla/5.0 (Macintosh; Intel Mac OS X) AppleWebKit/537.36 (KHTML, like Gecko) MCP/Playwright Safari/537.36'
    })
  }
  if (!page) page = await context.newPage()
  return page
}

async function closeBrowserIfAny() {
  try { if (browser) await browser.close() } catch {}
  browser = null
  context = null
  page = null
}

// ---------- Electron（Playwright Electron 驱动） ----------
/** @type {import('playwright').ElectronApplication|null} */ let electronApp = null
/** @type {import('playwright').Page|null} */ let electronPage = null

async function ensureElectronApp({ cwd, args }) {
  if (electronApp) return electronApp
  electronApp = await electron.launch({ args, cwd })
  electronPage = await electronApp.firstWindow()
  return electronApp
}

async function closeElectronIfAny() {
  try { if (electronApp) await electronApp.close() } catch {}
  electronApp = null
  electronPage = null
}

// ---------- 工具：Playwright 基本能力 ----------

// pw_open：打开页面并返回标题/URL/状态
server.registerTool(
  'pw_open',
  {
    description: 'Open a page with Chromium (headless by default) and return basic information.',
    inputSchema: z.object({
      url: z.string().url(),
      waitUntil: z.enum(['load', 'domcontentloaded', 'networkidle']).optional().default('domcontentloaded'),
      timeoutMs: z.number().int().positive().max(120000).optional().default(30000),
    })
  },
  async (_ctx, raw) => {
    const { url, waitUntil, timeoutMs } = parseArgs(
      z.object({
        url: z.string().url(),
        waitUntil: z.enum(['load', 'domcontentloaded', 'networkidle']).optional(),
        timeoutMs: z.number().int().positive().max(120000).optional(),
      })
    )(raw)
    const p = await ensurePage()
    const resp = await p.goto(url, { waitUntil: waitUntil || 'domcontentloaded', timeout: timeoutMs || 30000 })
    const title = await p.title().catch(() => '')
    return { ok: true, url: p.url(), title, status: resp?.status() ?? null }
  }
)

// pw_click：点击选择器
server.registerTool(
  'pw_click',
  {
    description: 'Click an element by selector on the current Chromium page.',
    inputSchema: z.object({
      selector: z.string().min(1),
      timeoutMs: z.number().int().positive().max(120000).optional().default(30000),
    })
  },
  async (_ctx, raw) => {
    const { selector, timeoutMs } = parseArgs(
      z.object({
        selector: z.string().min(1),
        timeoutMs: z.number().int().positive().max(120000).optional(),
      })
    )(raw)
    const p = await ensurePage()
    await p.click(selector, { timeout: timeoutMs || 30000 })
    return { ok: true }
  }
)

// pw_eval：执行 JS 或读取元素文本
server.registerTool(
  'pw_eval',
  {
    description: 'Evaluate JavaScript in the page context, or read element textContent if selector is provided.',
    inputSchema: z.object({
      code: z.string().optional(),
      selector: z.string().optional(),
    })
  },
  async (_ctx, raw) => {
    const { code, selector } = parseArgs(
      z.object({ code: z.string().optional(), selector: z.string().optional() })
    )(raw)
    if (!code && !selector) throw new Error('Either code or selector is required.')
    const p = await ensurePage()
    if (selector) {
      const txt = await p.locator(selector).first().textContent({ timeout: 10000 })
      return { ok: true, text: txt ?? '' }
    }
    const result = await p.evaluate(code)
    return { ok: true, result }
  }
)

// pw_screenshot：截图（base64）
server.registerTool(
  'pw_screenshot',
  {
    description: 'Take a screenshot of the current Chromium page and return it as base64.',
    inputSchema: z.object({
      fullPage: z.boolean().optional().default(false),
      quality: z.number().int().min(0).max(100).optional(), // 仅 jpg 生效
      type: z.enum(['png', 'jpeg']).optional().default('png'),
    })
  },
  async (_ctx, raw) => {
    const { fullPage, quality, type } = parseArgs(
      z.object({
        fullPage: z.boolean().optional(),
        quality: z.number().int().min(0).max(100).optional(),
        type: z.enum(['png', 'jpeg']).optional(),
      })
    )(raw)
    const p = await ensurePage()
    const buf = await p.screenshot({ fullPage: !!fullPage, quality, type })
    return { ok: true, imageBase64: buf.toString('base64') }
  }
)

// pw_close：关闭浏览器
server.registerTool(
  'pw_close',
  {
    description: 'Close Chromium browser instance.',
    inputSchema: z.object({}),
  },
  async () => {
    await closeBrowserIfAny()
    return { ok: true }
  }
)

// ---------- 工具：Electron 最小能力 ----------

