// electron_launch：拉起 Electron 应用（如 args:["."]，cwd=你的前端目录）
server.registerTool(
  'electron_launch',
  {
    description:
      'Launch an Electron app via Playwright Electron driver. Provide cwd to your app folder and args like ["."] (same as `electron .`).',
    inputSchema: z.object({
      cwd: z.string().min(1),
      args: z.array(z.string()).nonempty().default(['.']),
    })
  },
  async (_ctx, raw) => {
    const { cwd, args } = parseArgs(
      z.object({ cwd: z.string().min(1), args: z.array(z.string()).nonempty() })
    )(raw)
    await ensureElectronApp({ cwd, args })
    electronPage = await electronApp.firstWindow()
    const title = await electronPage.title().catch(() => '')
    const url = electronPage.url()
    return { ok: true, title, url }
  }
)

// electron_window_title：获取主窗口标题/URL
server.registerTool(
  'electron_window_title',
  {
    description: 'Get the title and URL of the first Electron BrowserWindow.',
    inputSchema: z.object({}),
  },
  async () => {
    if (!electronApp) throw new Error('Electron app not running.')
    electronPage = await electronApp.firstWindow()
    const title = await electronPage.title().catch(() => '')
    const url = electronPage.url()
    return { ok: true, title, url }
  }
)

// electron_eval：在渲染进程执行 JS（谨慎）
server.registerTool(
  'electron_eval',
  {
    description: 'Evaluate JavaScript in the first Electron BrowserWindow (renderer).',
    inputSchema: z.object({ code: z.string().min(1) }),
  },
  async (_ctx, raw) => {
    const { code } = parseArgs(z.object({ code: z.string().min(1) }))(raw)
    if (!electronApp) throw new Error('Electron app not running.')
    electronPage = await electronApp.firstWindow()
    const result = await electronPage.evaluate(code)
    return { ok: true, result }
  }
)

// electron_close：关闭 Electron 应用
server.registerTool(
  'electron_close',
  {
    description: 'Close the Electron application if running.',
    inputSchema: z.object({}),
  },
  async () => {
    await closeElectronIfAny()
    return { ok: true }
  }
)

// ---------- 启动（STDIO） ----------
await server.connect(new StdioServerTransport())
console.log('[mcp-playwright] ready: chromium + electron tools registered (name-first signature)')
