import { StdioClientTransport } from '@modelcontextprotocol/sdk/dist/esm/client/stdio.js';

const transport = new StdioClientTransport({
  command: 'node',
  args: ['server.mjs'],
  cwd: process.cwd(),
});

const client = new Client({ name: 'codex-cli', version: '1.0.0' });

async function main() {
  try {
    await client.connect(transport);
    const openResult = await client.callTool({
      name: 'pw_open',
      arguments: {
        url: 'https://example.com',
        waitUntil: 'domcontentloaded',
        timeoutMs: 15000,
      },
    });
    console.log(JSON.stringify({ tool: 'pw_open', result: openResult }));

    const closeResult = await client.callTool({
      name: 'pw_close',
      arguments: {},
    });
    console.log(JSON.stringify({ tool: 'pw_close', result: closeResult }));
  } finally {
    await client.close();
  }
}

main().catch((err) => {
  console.error('ERROR', err);
  process.exitCode = 1;
});
