{"output":"import { useMemo, useState } from 'react'

import { ReloadOutlined, PlayCircleOutlined, FolderOpenOutlined } from '@ant-design/icons'
import { Button, Space, Spin, Typography, App as AntdApp } from 'antd'

import { useMediaPreview } from '../../hooks/useMediaPreview'

const { Text } = Typography

export interface VideoPlayerProps {
  localPath?: string | null
  remoteUrl?: string | null
  mime?: string | null
  taskId?: string
}

const formatSize = (size?: number | null) => {
  if (!size || size <= 0) return null
  const mb = size / (1024 * 1024)
  if (mb >= 1) {
    return `${mb.toFixed(1)} MB`
  }
  const kb = size / 1024
  return `${kb.toFixed(0)} KB`
}

export const VideoPlayer = ({ localPath, remoteUrl, mime, taskId }: VideoPlayerProps) => {
  const { message } = AntdApp.useApp()
  const preview = useMediaPreview({ localPath, remoteUrl, mime })
  const [openLoading, setOpenLoading] = useState(false)
  const [revealLoading, setRevealLoading] = useState(false)

  const readableSize = useMemo(() => formatSize(preview.size), [preview.size])

  const handleOpen = async () => {
    if (!localPath) return
    const api = window.api?.media
    if (!api || typeof api.open !== 'function') {
      message.error('Electron 媒体接口未就绪，暂时无法外部打开')
      return
    }
    setOpenLoading(true)
    try {
      const response = await api.open({ path: localPath })
      if (!response.success) {
        message.error(response.error.message)
        return
      }
      message.success('已请求外部播放器打开')
    } catch (error) {
      const info = error instanceof Error ? error.message : String(error)
      message.error(info)
    } finally {
      setOpenLoading(false)
    }
  }

  const handleReveal = async () => {
    if (!localPath) return
    const api = window.api?.media
    if (!api || typeof api.reveal !== 'function') {
      message.error('Electron 媒体接口未就绪，暂时无法打开所在位置')
      return
    }
    setRevealLoading(true)
    try {
      const response = await api.reveal({ path: localPath })
      if (!response.success) {
        message.error(response.error.message)
        return
      }
      message.success('已打开文件所在目录')
    } catch (error) {
      const info = error instanceof Error ? error.message : String(error)
      message.error(info)
    } finally {
      setRevealLoading(false)
    }
  }

  const renderPreview = () => {
    if (preview.loading) {
      return (
        <Spin
          spinning
          style={{
            width: '100%',
            minHeight: 200,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}
        />
      )
    }

    if (preview.src) {
      return (
        <video
          key={preview.src}
          controls
          src={preview.src}
          preload="metadata"
          style={{
            width: '100%',
            maxHeight: 320,
            backgroundColor: '#000',
            borderRadius: 4
          }}
        />
      )
    }

    return (
      <div
        style={{
          width: '100%',
          minHeight: 200,
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center'
        }}
      >
        <Text type="secondary">暂未获取到可播放的视频，请稍后重试。</Text>
      </div>
    )
  }

  const showReload = Boolean(localPath)

  return (
    <Space direction="vertical" size="small" style={{ width: '100%' }}>
      <div
        style={{
          width: '100%',
          backgroundColor: '#141414',
          borderRadius: 6,
          minHeight: 200,
          overflow: 'hidden'
        }}
      >
        {renderPreview()}
      </div>

      {preview.error && <Text type="danger">{preview.error}</Text>}

      <Space size="small" wrap>
        {showReload && (
          <Button
            icon={<ReloadOutlined />}
            onClick={preview.reload}
            disabled={preview.loading}
          >
            重新加载
          </Button>
        )}
        {localPath && (
          <>
            <Button
              icon={<PlayCircleOutlined />}
              onClick={handleOpen}
              loading={openLoading}
              disabled={preview.loading}
            >
              外部播放器打开
            </Button>
            <Button
              icon={<FolderOpenOutlined />}
              onClick={handleReveal}
              loading={revealLoading}
              disabled={preview.loading}
            >
              打开所在位置
            </Button>
          </>
        )}
      </Space>

      <Text type="secondary">
        {taskId ? `任务 ${taskId.slice(-6)} · ` : ''}
        {preview.kind === 'local' ? '本地文件' : preview.kind === 'remote' ? '远程链接' : '无可用源'}
        {readableSize ? ` · ${readableSize}` : ''}
      </Text>
    </Space>
  )
}

export default VideoPlayer
