{"output":"import { useCallback, useEffect, useMemo, useState } from 'react'

import type { MediaPreviewResult } from '@shared/ipc-types'

export interface UseMediaPreviewOptions {
  localPath?: string | null
  remoteUrl?: string | null
  mime?: string | null
}

export interface MediaPreviewState {
  src: string | null
  kind: MediaPreviewResult['kind'] | null
  size?: number
  loading: boolean
  error: string | null
  reload: () => void
}

const INITIAL_STATE: Omit<MediaPreviewState, 'reload'> = {
  src: null,
  kind: null,
  size: undefined,
  loading: false,
  error: null
}

export const useMediaPreview = (options: UseMediaPreviewOptions): MediaPreviewState => {
  const { localPath, remoteUrl, mime } = options
  const [state, setState] = useState(INITIAL_STATE)
  const [reloadToken, setReloadToken] = useState(0)

  useEffect(() => {
    const normalizedLocal = typeof localPath === 'string' ? localPath.trim() : ''
    const normalizedRemote = typeof remoteUrl === 'string' ? remoteUrl.trim() : ''

    if (!normalizedLocal && !normalizedRemote) {
      setState(INITIAL_STATE)
      return
    }

    if (normalizedLocal) {
      let cancelled = false
      setState((prev) => ({
        ...prev,
        loading: true,
        error: null,
        src: null,
        kind: null
      }))

      const api = window.api?.media
      if (!api || typeof api.preview !== 'function') {
        setState({
          src: null,
          kind: null,
          size: undefined,
          loading: false,
          error: 'Electron 媒体接口未初始化'
        })
        return
      }

      api
        .preview({ path: normalizedLocal, mime: mime ?? null })
        .then((response) => {
          if (cancelled) return
          if (!response.success) {
            setState({
              src: null,
              kind: null,
              size: undefined,
              loading: false,
              error: response.error.message
            })
            return
          }
          setState({
            src: response.data.src,
            kind: response.data.kind,
            size: response.data.size,
            loading: false,
            error: null
          })
        })
        .catch((error) => {
          if (cancelled) return
          const message = error instanceof Error ? error.message : String(error)
          setState({
            src: null,
            kind: null,
            size: undefined,
            loading: false,
            error: message
          })
        })

      return () => {
        cancelled = true
      }
    }

    setState({
      src: normalizedRemote || null,
      kind: normalizedRemote ? 'remote' : null,
      size: undefined,
      loading: false,
      error: null
    })
  }, [localPath, remoteUrl, mime, reloadToken])

  const reload = useCallback(() => {
    setReloadToken((token) => token + 1)
  }, [])

  return useMemo(
    () => ({
      ...state,
      reload
    }),
    [state, reload]
  )
}
