{"output":"import { app, ipcMain, shell } from 'electron'
import { promises as fsp } from 'node:fs'
import path from 'node:path'
import { pathToFileURL } from 'node:url'

import type {
  IpcResponse,
  MediaOpenPayload,
  MediaPreviewPayload,
  MediaPreviewResult
} from '../../../../shared/ipc-types'

const PREVIEW_CHANNEL = 'media:preview'
const OPEN_CHANNEL = 'media:open'
const REVEAL_CHANNEL = 'media:reveal'

const ALLOWED_EXTENSIONS = new Set(['.mp4', '.mov', '.webm', '.mkv', '.jpg', '.jpeg', '.png', '.webp', '.gif'])

const createErrorResponse = <T>(
  code: string,
  message: string
): IpcResponse<T> => ({
  success: false,
  error: { code, message }
})

const createSuccessResponse = <T>(data: T): IpcResponse<T> => ({
  success: true,
  data
})

const resolveAllowedRoots = (): string[] => {
  const roots = new Set<string>()
  const envRoot = process.env.GEMENG_STORAGE_DIR
  if (envRoot) {
    roots.add(path.resolve(envRoot))
  }

  roots.add(path.join(app.getPath('userData'), 'seedance'))

  return Array.from(roots)
    .map((item) => {
      try {
        return path.resolve(item)
      } catch {
        return null
      }
    })
    .filter((item): item is string => Boolean(item))
}

const ensureAuthorizedPath = async (
  rawPath: string
): Promise<{ path: string; stats: Awaited<ReturnType<typeof fsp.stat>> }> => {
  const absolutePath = path.resolve(rawPath)
  const roots = resolveAllowedRoots()

  const isInside = roots.some((root) => {
    if (!root) return false
    const relative = path.relative(root, absolutePath)
    return relative === '' || (!relative.startsWith('..') && !path.isAbsolute(relative))
  })
  const allowLoose = (process.env.GEMENG_STORAGE_STRICT ?? '0') !== '1'

  try {
    const stats = await fsp.stat(absolutePath)
    if (!stats.isFile()) {
      throw Object.assign(new Error('目标不是有效文件'), { code: 'MEDIA_PREVIEW_INVALID' })
    }

    if (!isInside) {
      if (!allowLoose) {
        throw Object.assign(new Error('文件不在受信路径，无法直接预览'), {
          code: 'MEDIA_PREVIEW_DENIED'
        })
      }
      console.warn('[media] preview bypass root check', { path: absolutePath })
    }

    const extension = path.extname(absolutePath).toLowerCase()
    console.info('[media:preview] extension check', {
      path: absolutePath,
      extension,
      allowed: ALLOWED_EXTENSIONS.has(extension)
    })
    if (extension && !ALLOWED_EXTENSIONS.has(extension)) {
      throw Object.assign(new Error(`文件类型不受支持: ${extension}`), { code: 'MEDIA_PREVIEW_UNSUPPORTED' })
    }

    return { path: absolutePath, stats }
  } catch (error) {
    const err = error as NodeJS.ErrnoException
    if (err.code === 'ENOENT') {
      throw Object.assign(new Error('文件不存在或尚未落盘'), { code: 'MEDIA_PREVIEW_NOT_FOUND' })
    }
    throw Object.assign(new Error(err.message || '读取文件失败'), { code: 'MEDIA_PREVIEW_IO_ERROR' })
  }
}

const toPreviewResult = async (
  payload: MediaPreviewPayload
): Promise<IpcResponse<MediaPreviewResult>> => {
  if (!payload?.path || typeof payload.path !== 'string') {
    return createErrorResponse('MEDIA_PREVIEW_INVALID', '缺少有效的文件路径')
  }

  const maybeUrl = payload.path.trim()
  if (/^https?:\/\//i.test(maybeUrl)) {
    return createSuccessResponse({
      src: maybeUrl,
      kind: 'remote',
      mime: payload.mime ?? null
    })
  }

  try {
    const { path: authorizedPath, stats } = await ensureAuthorizedPath(payload.path)
    const fileUrl = pathToFileURL(authorizedPath).toString()
    return createSuccessResponse({
      src: fileUrl,
      kind: 'local',
      mime: payload.mime ?? null,
      size: stats.size
    })
  } catch (error) {
    const err = error as { message?: string; code?: string }
    const code = typeof err.code === 'string' ? err.code : 'MEDIA_PREVIEW_FAILED'
    const message = err.message || '预览失败，请稍后重试'
    return createErrorResponse(code, message)
  }
}

const handleOpen = async (
  payload: MediaOpenPayload
): Promise<IpcResponse<void>> => {
  if (!payload?.path) {
    return createErrorResponse('MEDIA_OPEN_INVALID', '缺少文件路径')
  }

  try {
    const { path: authorizedPath } = await ensureAuthorizedPath(payload.path)
    const result = await shell.openPath(authorizedPath)
    if (result) {
      return createErrorResponse('MEDIA_OPEN_FAILED', result)
    }
    return createSuccessResponse<void>(undefined)
  } catch (error) {
    const err = error as { message?: string; code?: string }
    const code = typeof err.code === 'string' ? err.code : 'MEDIA_OPEN_FAILED'
    const message = err.message || '无法打开文件'
    return createErrorResponse(code, message)
  }
}

const handleReveal = async (
  payload: MediaOpenPayload
): Promise<IpcResponse<void>> => {
  if (!payload?.path) {
    return createErrorResponse('MEDIA_REVEAL_INVALID', '缺少文件路径')
  }

  try {
    const { path: authorizedPath } = await ensureAuthorizedPath(payload.path)
    shell.showItemInFolder(authorizedPath)
    return createSuccessResponse<void>(undefined)
  } catch (error) {
    const err = error as { message?: string; code?: string }
    const code = typeof err.code === 'string' ? err.code : 'MEDIA_REVEAL_FAILED'
    const message = err.message || '无法打开所在位置'
    return createErrorResponse(code, message)
  }
}

export const registerMediaHandlers = () => {
  ipcMain.removeHandler(PREVIEW_CHANNEL)
  ipcMain.handle(PREVIEW_CHANNEL, (_event, payload: MediaPreviewPayload) =>
    toPreviewResult(payload)
  )

  ipcMain.removeHandler(OPEN_CHANNEL)
  ipcMain.handle(OPEN_CHANNEL, (_event, payload: MediaOpenPayload) =>
    handleOpen(payload)
  )

  ipcMain.removeHandler(REVEAL_CHANNEL)
  ipcMain.handle(REVEAL_CHANNEL, (_event, payload: MediaOpenPayload) =>
    handleReveal(payload)
  )
}
