import { immer } from 'zustand/middleware/immer'

import type {
  EnvelopeSummaryPayload,
  HistoryListPayload,
  HistoryRecord as HistoryEntry
} from '@shared/ipc-types'

import { getModelLabel, type ModelKey } from '../constants/schema'
import type { TaskRecord, TaskStatus } from './taskOrchestrator'

type HistoryTab = 'image' | 'video'

interface HistoryGroupState {
  items: TaskRecord[]
  cursor: string | null
  hasMore: boolean
  loading: boolean
  initialized: boolean
  storageRoot: string | null
}

interface HistoryStoreState {
  groups: Record<HistoryTab, HistoryGroupState>
  lastError: string | null
  ensureLoaded: (recordType: HistoryTab) => Promise<void>
  loadMore: (recordType: HistoryTab) => Promise<void>
  refresh: (recordType?: HistoryTab) => Promise<void>
  clear: () => void
}

const DEFAULT_LIMIT = 40

const MODEL_KEY_FALLBACK: Record<HistoryTab, ModelKey> = {
  image: 'image-3.0',
  video: 'video-pro'
}

const MODEL_KEY_SET = new Set<ModelKey>(['image-3.0', 'image-4.0', 'video-pro', 'video-lite'])

const normalizePath = (value?: string | null): string | undefined => {
  if (typeof value !== 'string' || !value.trim()) return undefined
  return value.replace(/\\/g, '/').trim()
}

const joinPath = (root?: string | null, relative?: string | null): string | undefined => {
