  const normalizedRelative = normalizePath(relative)
  if (!normalizedRoot || !normalizedRelative) return undefined

  const trimmedRoot = normalizedRoot.replace(/\/+$/, '')
  const trimmedRelative = normalizedRelative.replace(/^\/+/, '')
  return `${trimmedRoot}/${trimmedRelative}`
}

