}

const normalizeStatus = (status?: string | null, existsLocal?: boolean): TaskStatus => {
  if (existsLocal === false) {
    return 'FAILED'
  }
  const raw = (status ?? '').toLowerCase()
  if (!raw) return 'SUCCEEDED'
  if (['succeeded', 'success', 'completed', 'complete', 'done'].includes(raw)) {
    return 'SUCCEEDED'
  }
  if (['failed', 'error', 'errored'].includes(raw)) {
    return 'FAILED'
  }
  if (['cancelled', 'canceled'].includes(raw)) {
    return 'CANCELLED'
  }
  if (['running', 'processing', 'in_progress'].includes(raw)) {
    return 'RUNNING'
  }
  if (['pending', 'queued', 'waiting'].includes(raw)) {
    return 'PENDING'
  }
  return 'SUCCEEDED'
}

const basename = (path?: string): string | undefined => {
  const normalized = normalizePath(path)
  if (!normalized) return undefined
  const trimmed = normalized.replace(/\/+$/g, '')
  const parts = trimmed.split('/')
  return parts.length ? parts[parts.length - 1] : undefined
}

