        with open(csv_path, 'w', encoding='utf-8', newline='') as f:
            if content_type == 'chats':
                fieldnames = self.CHAT_FIELDS
            else:
                fieldnames = self.CSV_FIELDS_V21
            writer = csv.DictWriter(f, fieldnames=fieldnames)
            writer.writeheader()
            for row in records:
                writer.writerow({field: row.get(field, '') for field in fieldnames})

    def _ensure_relative_path(self, path: Union[str, Path, None]) -> str:
        if not path:
            return ''
        try:
            rel = Path(path).resolve().relative_to(self.root_path)
            return str(rel).replace('\\', '/')
        except Exception:
            return ''

    def _thumbnail_root(self, content_type: str) -> Path:
        subdir = 'images' if content_type == 'images' else 'videos'
        root = self.root_path / 'thumbnails' / subdir
