export const useHistoryStore = create<HistoryStoreState>()(
  immer((set, get) => {
    const load = async (recordType: HistoryTab, options: { reset: boolean }) => {
      const state = get()
      const group = state.groups[recordType]
      if (group.loading) return

      set((draft) => {
        draft.lastError = null
        draft.groups[recordType].loading = true
        if (options.reset) {
          draft.groups[recordType].cursor = null
          draft.groups[recordType].hasMore = true
          draft.groups[recordType].items = []
        }
      })

      try {
        const payload: HistoryListPayload = {
          recordType,
          limit: DEFAULT_LIMIT
        }
        if (!options.reset && group.cursor) {
          payload.cursor = group.cursor
        }

        const response = await window.api.history.list(payload)
        if (!response.success) {
          throw new Error(response.error.message)
        }

        const { items, nextCursor, storageRoot } = response.data
        const mapped = items.map((item) => mapHistoryRecordToTask(item))

        set((draft) => {
          const target = draft.groups[recordType]
          const existingIds = new Set<string>()
          if (!options.reset) {
            target.items.forEach((task) => existingIds.add(task.id))
          }
          const merged = options.reset ? [] : [...target.items]
          mapped.forEach((task) => {
            if (!existingIds.has(task.id)) {
              merged.push(task)
              existingIds.add(task.id)
            }
          })
          merged.sort((a, b) => (b.updatedAt ?? b.createdAt) - (a.updatedAt ?? a.createdAt))

          target.items = merged
          target.cursor = nextCursor ?? null
          target.hasMore = Boolean(nextCursor)
          target.loading = false
          target.initialized = true
          target.storageRoot = storageRoot ?? target.storageRoot
        })
      } catch (error) {
        const message = error instanceof Error ? error.message : String(error)
        set((draft) => {
          draft.groups[recordType].loading = false
          draft.lastError = message
        })
      }
    }

    return {
      groups: {
        image: initialGroup(),
        video: initialGroup()
      },
      lastError: null,
      ensureLoaded: async (recordType: HistoryTab) => {
        const group = get().groups[recordType]
        if (group.initialized || group.loading) return
        await load(recordType, { reset: true })
      },
      loadMore: async (recordType: HistoryTab) => {
        const group = get().groups[recordType]
        if (!group.hasMore || group.loading) return
        await load(recordType, { reset: false })
      },
      refresh: async (recordType?: HistoryTab) => {
        if (recordType) {
          await load(recordType, { reset: true })
        } else {
          await Promise.all((['image', 'video'] as const).map((kind) => load(kind, { reset: true })))
        }
      },
      clear: () => {
        set({
          groups: {
            image: initialGroup(),
            video: initialGroup()
          },
          lastError: null
        })
