const resolveRepoRoot = (): string => {
  const appRoot = process.env.APP_ROOT ?? join(__dirname, '..')
  const candidates = [
    resolvePath(appRoot, '..'),
    resolvePath(appRoot, '..', '..'),
    appRoot
  ]

  for (const candidate of candidates) {
    const script = join(candidate, 'tools', 'history_cli.py')
    if (existsSync(script)) {
      return candidate
    }
  }

  return appRoot
}

const runHistoryCommand = async (args: string[]): Promise<Record<string, unknown>> => {
  const python = process.env.SEEDREAM_PYTHON ?? 'python3'
  const repoRoot = resolveRepoRoot()
  const script = join(repoRoot, 'tools', 'history_cli.py')

  if (!existsSync(script)) {
    throw new Error('未找到 tools/history_cli.py')
  }

  return new Promise<Record<string, unknown>>((resolve, reject) => {
    const child = spawn(python, [script, ...args], {
      cwd: repoRoot,
      env: {
        ...process.env,
        LOGURU_LEVEL: process.env.LOGURU_LEVEL ?? 'ERROR'
      },
      stdio: ['ignore', 'pipe', 'pipe']
    })

    let stdout = ''
    let stderr = ''

    child.stdout.on('data', (chunk) => {
      stdout += chunk.toString()
    })

    child.stderr.on('data', (chunk) => {
      stderr += chunk.toString()
    })

    child.once('error', (error) => {
      reject(error)
    })

    child.once('close', (code) => {
      if (code !== 0) {
        const merged = stderr.trim() || stdout.trim()
        reject(new Error(merged || `history 命令失败 (code=${code})`))
        return
      }

      const output = stdout.trim()
      if (!output) {
