        if ctype == 'image':
            ctype = 'images'
        elif ctype == 'video':
            ctype = 'videos'
        item['content_type'] = ctype

        # 解析JSON字段
        item['parameters'] = self._ensure_dict(item.get('parameters'))
        item['metadata'] = self._ensure_dict(item.get('metadata'))
        item['batch_info'] = self._ensure_dict(item.get('batch_info'))

        metadata = item['metadata']

        # 存储根目录（统一返回绝对路径）
        root_candidate = getattr(self.metadata_manager, 'root_path', None) or self.storage_root
        try:
            root_path = Path(root_candidate).resolve()
        except Exception:
            root_path = Path(self.storage_root).resolve()
        root_str = str(root_path)
        item['storage_root'] = root_str

        # 同步核心字段
        created_at = item.get('created_at')
        if isinstance(created_at, datetime):
            item['created_at'] = created_at.isoformat()

        file_id = item.get('file_id') or item.get('generation_id') or metadata.get('task_id')
